     5// Screenshot functionality implementation
     6function setupScreenshotFeature() {
     7  const takeScreenshotBtn = document.getElementById('take-screenshot');
     8  const clearScreenshotsBtn = document.getElementById('clear-screenshots');
     9  const screenshotsContainer = document.getElementById('screenshots-container');
    10  const screenshotCount = document.getElementById('screenshot-count');
    11    
    12  let screenshots = [];
    13    
    14  // Update screenshot count display
    15  function updateScreenshotCount() {
    16    screenshotCount.textContent = `${screenshots.length} image${screenshots.length !== 1 ? 's' : ''}`;
    17  }
    18    
    19  // Format timestamp for display
    20  function formatTimestamp(timestamp) {
    21    const date = new Date(timestamp);
    22    return date.toLocaleString();
    23  }
    24    
    25  // Create screenshot item element
    26  function createScreenshotItem(screenshot) {
    27    const item = document.createElement('div');
    28    item.className = 'screenshot-item';
    29    item.dataset.timestamp = screenshot.timestamp;
    30        
    31    const pathDisplay = screenshot.filepath !== 'memory-only' ? screenshot.relativePath : 'Stored in memory only';
    32        
    33    item.innerHTML = `
    34            <div class="screenshot-header" onclick="toggleScreenshot('${screenshot.id}')">
    35                <div class="screenshot-info">
    36                    <span class="screenshot-title"> Screenshot</span>
    37                    <span class="screenshot-timestamp">${formatTimestamp(screenshot.timestamp)}</span>
    38                </div>
    39                <div class="screenshot-controls">
    40                    <button onclick="event.stopPropagation(); copyScreenshotPath('${screenshot.id}')" class="screenshot-btn" title="Copy file path"> Copy Path</button>
    41                    <button onclick="event.stopPropagation(); downloadScreenshot('${screenshot.id}')" class="screenshot-btn"> Save</button>
    42                    <button onclick="event.stopPropagation(); deleteScreenshot('${screenshot.id}')" class="screenshot-btn"> Delete</button>
    43                    <span class="screenshot-toggle"></span>
    44                </div>
    45            </div>
    46            <div class="screenshot-content" id="screenshot-content-${screenshot.id}">
    47                <div class="screenshot-path">
    48                    <small><strong>Path:</strong> <code onclick="copyToClipboard('${screenshot.relativePath}')" title="Click to copy">${pathDisplay}</code></small>
    49                </div>
    50                <img src="${screenshot.dataUrl}" alt="Screenshot ${formatTimestamp(screenshot.timestamp)}" />
    51            </div>
    52        `;
    53        
    54    return item;
    55  }
    56    
    57  // Toggle screenshot collapse/expand
    58  window.toggleScreenshot = function(id) {
    59    const content = document.getElementById(`screenshot-content-${id}`);
    60    const toggle = content.parentElement.querySelector('.screenshot-toggle');
    61        
    62    if (content.style.display === 'none') {
    63      content.style.display = 'block';
    64      toggle.textContent = '';
    65    } else {
    66      content.style.display = 'none';
    67      toggle.textContent = '';
    68    }
    69  };
    70    
    71  // Copy screenshot path to clipboard
    72  window.copyScreenshotPath = function(id) {
    73    const screenshot = screenshots.find(s => s.id === id);
    74    if (screenshot && screenshot.relativePath !== 'memory-only') {
    75      copyToClipboard(screenshot.relativePath);
    76    } else {
    77      copyToClipboard('Screenshot stored in memory only - no file path available');
    78    }
    79  };
    80    
    81  // Copy text to clipboard
    82  window.copyToClipboard = function(text) {
    83    if (navigator.clipboard) {
    84      navigator.clipboard.writeText(text).then(() => {
    85        console.log(' Copied to clipboard:', text);
    86        // Show temporary feedback
    87        const toast = document.createElement('div');
    88        toast.textContent = 'Copied to clipboard!';
    89        toast.style.cssText = `
    90                    position: fixed;
    91                    top: 20px;
    92                    right: 20px;
    93                    background: #28a745;
    94                    color: white;
    95                    padding: 10px 15px;
    96                    border-radius: 5px;
    97                    z-index: 10000;
    98                    font-size: 14px;
    99                `;
   100        document.body.appendChild(toast);
   101        setTimeout(() => document.body.removeChild(toast), 2000);
   102      }).catch(err => {
   103        console.error('Failed to copy:', err);
   104      });
   105    } else {
   106      // Fallback for older browsers
   107      const textArea = document.createElement('textarea');
   108      textArea.value = text;
   109      document.body.appendChild(textArea);
   110      textArea.select();
   111      document.execCommand('copy');
   112      document.body.removeChild(textArea);
   113      console.log(' Copied to clipboard (fallback):', text);
   114    }
   115  };
   116    
   117  // Download screenshot
   118  window.downloadScreenshot = function(id) {
   119    const screenshot = screenshots.find(s => s.id === id);
   120    if (screenshot) {
   121      const link = document.createElement('a');
   122      link.href = screenshot.dataUrl;
   123      link.download = `screenshot-${formatTimestamp(screenshot.timestamp).replace(/[^\w-]/g, '_')}.png`;
   124      link.click();
   125    }
   126  };
   127    
   128  // Delete screenshot (including file)
   129  window.deleteScreenshot = async function(id) {
   130    const index = screenshots.findIndex(s => s.id === id);
   131    if (index !== -1) {
   132      const screenshot = screenshots[index];
   133            
   134      try {
   135        // Delete file if it exists
   136        if (screenshot.filepath !== 'memory-only' && window.electronAPI && window.electronAPI.deleteFile) {
   137          await window.electronAPI.deleteFile(screenshot.filepath);
   138          console.log(` Deleted file: ${screenshot.filepath}`);
   139        }
   140      } catch (error) {
   141        console.warn(' Failed to delete screenshot file:', error);
   142      }
   143            
   144      // Remove from memory
   145      screenshots.splice(index, 1);
   146      renderScreenshots();
   147      console.log(' Screenshot removed from collection');
   148    }
   149  };
   150    
   151  // Render all screenshots
   152  function renderScreenshots() {
   153    if (screenshots.length === 0) {
   154      screenshotsContainer.innerHTML = `
   155                <div class="no-screenshots">
   156                    <span class="no-screenshots-icon"></span>
   157                    <p>No screenshots captured yet</p>
   158                    <small>Click "Take Screenshot" to capture and store images</small>
   159                </div>
   160            `;
   161    } else {
   162      screenshotsContainer.innerHTML = '';
   163      screenshots.forEach(screenshot => {
   164        screenshotsContainer.appendChild(createScreenshotItem(screenshot));
   165      });
   166    }
   167    updateScreenshotCount();
   168  }
   169    
   170  // Generate session ID for tmp directory in chrome-container root
   171  const sessionId = Date.now().toString();
   172  const tmpDir = `../tmp/session-${sessionId}`;
   173    
   174  // Capture screenshot using proper screen capture
   175  async function captureScreenshot() {
   176    try {
   177      let dataUrl = null;
   178            
   179      // Method 1: Try Electron's nativeImage screen capture
   180      if (window.electronAPI && window.electronAPI.captureScreen) {
   181        console.log(' Using Electron screen capture API');
   182        dataUrl = await window.electronAPI.captureScreen();
   183      }
   184      // Method 2: Try getDisplayMedia (requires user permission)
   185      else if (navigator.mediaDevices && navigator.mediaDevices.getDisplayMedia) {
   186        console.log(' Using getDisplayMedia API');
   187        const stream = await navigator.mediaDevices.getDisplayMedia({ 
   188          video: { 
   189            mediaSource: 'screen',
   190            width: { ideal: 1920 },
   191            height: { ideal: 1080 },
   192          }, 
   193        });
   194                
   195        const video = document.createElement('video');
   196        video.srcObject = stream;
   197        video.play();
   198                
   199        // Wait for video to load
   200        await new Promise((resolve) => {
   201          video.addEventListener('loadedmetadata', resolve, { once: true });
   202        });
   203                
   204        // Create canvas and capture frame
   205        const canvas = document.createElement('canvas');
   206        const ctx = canvas.getContext('2d');
   207        canvas.width = video.videoWidth || 1920;
   208        canvas.height = video.videoHeight || 1080;
   209        ctx.drawImage(video, 0, 0);
   210                
   211        dataUrl = canvas.toDataURL('image/png', 1.0);
   212                
   213        // Stop the stream
   214        stream.getTracks().forEach(track => track.stop());
   215      }
   216      // Method 3: Try html2canvas if available
   217      else if (window.html2canvas) {
   218        console.log(' Using html2canvas');
   219        const canvas = await html2canvas(document.body, {
   220          height: window.innerHeight,
   221          width: window.innerWidth,
   222          scrollX: 0,
   223          scrollY: 0,
   224        });
   225        dataUrl = canvas.toDataURL('image/png', 1.0);
   226      }
   227      // Method 4: Canvas screenshot of current viewport
   228      else {
   229        console.log(' Using canvas viewport capture');
   230        const canvas = document.createElement('canvas');
   231        const ctx = canvas.getContext('2d');
   232        canvas.width = window.innerWidth;
   233        canvas.height = window.innerHeight;
   234                
   235        // Capture DOM content to canvas (basic method)
   236        ctx.fillStyle = getComputedStyle(document.body).backgroundColor || '#ffffff';
   237        ctx.fillRect(0, 0, canvas.width, canvas.height);
   238                
   239        // Add text overlay showing what was captured
   240        ctx.fillStyle = '#333333';
   241        ctx.font = '16px Arial';
   242        ctx.textAlign = 'center';
   243        ctx.fillText('Viewport Screenshot', canvas.width / 2, 50);
   244        ctx.fillText(`${canvas.width}x${canvas.height}`, canvas.width / 2, 80);
   245        ctx.fillText(formatTimestamp(Date.now()), canvas.width / 2, 110);
   246                
   247        dataUrl = canvas.toDataURL('image/png', 1.0);
   248      }
   249            
   250      if (dataUrl && dataUrl !== 'data:,') {
   251        await saveScreenshotFile(dataUrl);
   252      } else {
   253        throw new Error('Failed to capture screenshot data');
   254      }
   255            
   256    } catch (error) {
   257      console.error('Screenshot capture failed:', error);
   258            
   259      // Create error image
   260      const canvas = document.createElement('canvas');
   261      const ctx = canvas.getContext('2d');
   262      canvas.width = 800;
   263      canvas.height = 400;
   264      ctx.fillStyle = '#f8f9fa';
   265      ctx.fillRect(0, 0, canvas.width, canvas.height);
   266      ctx.strokeStyle = '#dee2e6';
   267      ctx.strokeRect(0, 0, canvas.width, canvas.height);
   268            
   269      ctx.fillStyle = '#dc3545';
   270      ctx.font = 'bold 24px Arial';
   271      ctx.textAlign = 'center';
   272      ctx.fillText('Screenshot Capture Failed', canvas.width / 2, canvas.height / 2 - 40);
   273            
   274      ctx.fillStyle = '#6c757d';
   275      ctx.font = '16px Arial';
   276      ctx.fillText(`Error: ${error.message}`, canvas.width / 2, canvas.height / 2);
   277      ctx.fillText('Try using browser screen share permission', canvas.width / 2, canvas.height / 2 + 30);
   278      ctx.fillText(formatTimestamp(Date.now()), canvas.width / 2, canvas.height / 2 + 60);
   279            
   280      const errorDataUrl = canvas.toDataURL('image/png', 1.0);
   281      await saveScreenshotFile(errorDataUrl);
   282    }
   283  }
   284    
   285  // Save screenshot as file and add to collection
   286  async function saveScreenshotFile(dataUrl) {
   287    const timestamp = Date.now();
   288    const filename = `screenshot-${timestamp}.png`;
   289    const filepath = `${tmpDir}/${filename}`;
   290        
   291    try {
   292      console.log(' Checking electronAPI availability:', {
   293        electronAPI: !!window.electronAPI,
   294        ensureDir: !!(window.electronAPI && window.electronAPI.ensureDir),
   295        saveFile: !!(window.electronAPI && window.electronAPI.saveFile),
   296      });
   297            
   298      let fileSaved = false;
   299            
   300      // Create tmp directory if it doesn't exist
   301      if (window.electronAPI && window.electronAPI.ensureDir) {
   302        const dirResult = await window.electronAPI.ensureDir(tmpDir);
   303        console.log(' Directory creation result:', dirResult);
   304      }
   305            
   306      // Convert dataUrl to buffer and save file
   307      const base64Data = dataUrl.replace(/^data:image\/png;base64,/, '');
   308            
   309      if (window.electronAPI && window.electronAPI.saveFile) {
   310        const saveResult = await window.electronAPI.saveFile(filepath, base64Data, 'base64');
   311        console.log(' File save result:', saveResult);
   312        if (saveResult && saveResult.success) {
   313          fileSaved = true;
   314          console.log(` Screenshot saved to: ${filepath}`);
   315        } else {
   316          console.warn(' File save failed:', saveResult);
   317        }
   318      } else {
   319        console.warn(' File system API not available, storing in memory only');
   320      }
   321            
   322      const screenshot = {
   323        id: timestamp.toString(),
   324        timestamp: timestamp,
   325        filename: filename,
   326        filepath: fileSaved ? filepath : 'memory-only',
   327        dataUrl: dataUrl, // Keep for display
   328        relativePath: fileSaved ? `chrome-container-app/${filepath}` : 'memory-only',
   329      };
   330            
   331      screenshots.unshift(screenshot); // Add to beginning
   332      renderScreenshots();
   333            
   334      console.log(' Screenshot captured and added to collection');
   335            
   336    } catch (error) {
   337      console.error(' Failed to save screenshot file:', error);
   338            
   339      // Fallback: store in memory only
   340      const screenshot = {
   341        id: timestamp.toString(),
   342        timestamp: timestamp,
   343        filename: filename,
   344        filepath: 'memory-only',
   345        dataUrl: dataUrl,
   346        relativePath: 'memory-only',
   347      };
   348            
   349      screenshots.unshift(screenshot);
   350      renderScreenshots();
   351    }
   352  }
   353    
   354  // Event listeners
   355  if (takeScreenshotBtn) {
   356    takeScreenshotBtn.addEventListener('click', async () => {
   357      takeScreenshotBtn.disabled = true;
   358      takeScreenshotBtn.textContent = ' Capturing...';
   359            
   360      try {
   361        await captureScreenshot();
   362      } catch (error) {
   363        console.error('Screenshot button error:', error);
   364      } finally {
   365        takeScreenshotBtn.disabled = false;
   366        takeScreenshotBtn.textContent = ' Take Screenshot';
   367      }
   368    });
   369  }
   370    
   371  if (clearScreenshotsBtn) {
   372    clearScreenshotsBtn.addEventListener('click', async () => {
   373      if (screenshots.length > 0 && confirm(`Delete all ${screenshots.length} screenshots and remove session folder?`)) {
   374        try {
   375          // Delete individual files first
   376          for (const screenshot of screenshots) {
   377            if (screenshot.filepath !== 'memory-only' && window.electronAPI && window.electronAPI.deleteFile) {
   378              try {
   379                await window.electronAPI.deleteFile(screenshot.filepath);
   380              } catch (err) {
   381                console.warn(`Failed to delete ${screenshot.filepath}:`, err);
   382              }
   383            }
   384          }
   385                    
   386          // Delete the entire session directory
   387          if (window.electronAPI && window.electronAPI.deleteDir) {
   388            try {
   389              await window.electronAPI.deleteDir(tmpDir);
   390              console.log(` Deleted session directory: ${tmpDir}`);
   391            } catch (err) {
   392              console.warn(`Failed to delete directory ${tmpDir}:`, err);
   393            }
   394          }
   395                    
   396        } catch (error) {
   397          console.error('Error during cleanup:', error);
   398        }
   399                
   400        screenshots = [];
   401        renderScreenshots();
   402        console.log(' All screenshots cleared and session directory removed');
   403      }
   404    });
   405  }
   406    
   407  // Initial render
   408  renderScreenshots();
   409    
   410  console.log(' Screenshot feature initialized');
   411}
