   413// --- 1. GET LIBRARIES AND UI ELEMENTS ---
   414// Wait for DOM and electronAPI to be available
   415document.addEventListener('DOMContentLoaded', () => {
   416  console.log(' Checking window.electronAPI availability...');
   417  console.log('window.electronAPI:', window.electronAPI);
   418  console.log('Available window properties:', Object.keys(window));
   419    
   420  // Declare terminal variables at higher scope for tab switching access
   421  let term = null;
   422  let fitAddon = null;
   423    
   424  if (!window.electronAPI) {
   425    console.error(' window.electronAPI is not available');
   426    console.error('This means the preload script failed to load or contextBridge failed');
   427    alert('FATAL ERROR: Preload script failed to load.\n\nCheck browser console for details.');
   428    return;
   429  }
   430    
   431  console.log(' window.electronAPI is available with keys:', Object.keys(window.electronAPI));
   432    
   433  // Test the bridge connection
   434  if (window.electronAPI.testConnection) {
   435    console.log(' Bridge test:', window.electronAPI.testConnection());
   436  }
   437
   438  // Check if Terminal libraries are available (from global scope or electronAPI)
   439  // Note: CDN version has FitAddon.FitAddon constructor
   440  const terminalLibrariesAvailable = (window.Terminal && (window.FitAddon || window.FitAddon?.FitAddon)) || 
   441                                      (window.electronAPI?.Terminal && window.electronAPI?.FitAddon);
   442  if (!terminalLibrariesAvailable) {
   443    console.log(' Terminal libraries not available - checking for globals...');
   444    console.log('window.Terminal:', typeof window.Terminal);
   445    console.log('window.FitAddon:', typeof window.FitAddon);
   446    console.log('window.FitAddon?.FitAddon:', typeof window.FitAddon?.FitAddon);
   447  }
   448
   449  // Get all element references from the HTML
   450  const initializeBtn = document.getElementById('initialize-chrome');
   451  const restartBtn = document.getElementById('restart-chrome');
   452  const reconnectDevToolsBtn = document.getElementById('reconnect-devtools');
   453  const canvasBtn = document.getElementById('navigate-canvas');
   454  const chromePlaceholder = document.getElementById('chrome-placeholder');
   455  const chromeContainer = document.querySelector('.chrome-container');
   456  let chromeViewport = document.getElementById('chrome-viewport'); // Use 'let' to allow reassignment
   457  const chromeStatusIndicator = document.getElementById('chrome-status');
   458  const cdpStatus = document.getElementById('cdp-connection');
   459  const debugPortDisplay = document.getElementById('debug-port');
   460  const memoryDisplay = document.getElementById('memory-usage');
   461  const importBtn = document.getElementById('import-md');
   462  const guidanceContent = document.getElementById('guidance-content');
   463  const tabButtons = document.querySelectorAll('.tab-btn');
   464  const tabContents = document.querySelectorAll('.tab-content');
   465  const terminalContainer = document.getElementById('terminal');
   466
   467  // Message elements
   468  const userMessageInput = document.getElementById('user-message');
   469  const sendMessageBtn = document.getElementById('send-message');
   470  const queueMessageBtn = document.getElementById('queue-message');
   471  const urgentMessageBtn = document.getElementById('urgent-message');
   472  const queuedMessagesContainer = document.getElementById('queued-messages');
   473
   474  // Error log elements
   475  const errorLog = document.getElementById('error-log');
   476  const clearErrorsBtn = document.getElementById('clear-errors');
   477  const exportErrorsBtn = document.getElementById('export-errors');
   478  const totalErrorsDisplay = document.getElementById('total-errors');
   479  const chromeErrorsDisplay = document.getElementById('chrome-errors');
   480  const appErrorsDisplay = document.getElementById('app-errors');
   481
   482  // Session management elements
   483  const clearSessionsBtn = document.getElementById('clear-sessions');
   484  const refreshStatusBtn = document.getElementById('refresh-status');
   485  const exportLogsBtn = document.getElementById('export-logs');
   486
   487  // Performance metrics elements
   488  const cpuUsageDisplay = document.getElementById('cpu-usage');
   489  const liveConnectionsDisplay = document.getElementById('live-connections');
   490  const chromeTabsDisplay = document.getElementById('chrome-tabs');
   491
   492  // AI tracking elements
   493  const aiStatusIcon = document.getElementById('ai-status-icon');
   494  const aiStatusText = document.getElementById('ai-status-text');
   495  const aiLastAction = document.getElementById('ai-last-action');
   496
   497  // --- 2. THE CENTRAL UI UPDATE FUNCTION ---
   498  function updateUI(status) {
   499    if (!status) return;
   500    console.log(' Updating UI with live metrics:', status);
   501
   502    // --- Update Live Performance Metrics ---
   503    if (status.cpu !== undefined) {
   504      if (cpuUsageDisplay) cpuUsageDisplay.textContent = `${status.cpu}%`;
   505      if (document.getElementById('cpu-bar')) {
   506        const cpuBar = document.getElementById('cpu-bar');
   507        cpuBar.style.width = `${status.cpu}%`;
   508        cpuBar.style.transition = 'width 0.5s ease';
   509        // Color code based on CPU usage
   510        if (status.cpu > 80) cpuBar.style.backgroundColor = '#e74c3c';
   511        else if (status.cpu > 50) cpuBar.style.backgroundColor = '#f39c12';
   512        else cpuBar.style.backgroundColor = '#27ae60';
   513      }
   514    }
   515        
   516    if (status.memory !== undefined) {
   517      if (memoryDisplay) memoryDisplay.textContent = `${status.memory} MB`;
   518      if (document.getElementById('memory-bar')) {
   519        const memoryBar = document.getElementById('memory-bar');
   520        // Animate the bar, assuming a max of 1024MB for visualization
   521        const memPercentage = Math.min(100, (status.memory / 1024) * 100);
   522        memoryBar.style.width = `${memPercentage}%`;
   523        memoryBar.style.transition = 'width 0.5s ease';
   524        // Color code based on memory usage
   525        if (memPercentage > 80) memoryBar.style.backgroundColor = '#e74c3c';
   526        else if (memPercentage > 50) memoryBar.style.backgroundColor = '#f39c12';
   527        else memoryBar.style.backgroundColor = '#3498db';
   528      }
   529    }
   530        
   531    if (status.chromeTabs !== undefined) {
   532      if (chromeTabsDisplay) chromeTabsDisplay.textContent = status.chromeTabs;
   533    }
   534        
   535    if (status.connections !== undefined) {
   536      if (liveConnectionsDisplay) liveConnectionsDisplay.textContent = status.connections;
   537    }
   538
   539    // --- Update Chrome Status (existing logic) ---
   540    if (status.running) {
   541      // Chrome is running - SHOW the viewport, HIDE the placeholder
   542      chromePlaceholder.classList.add('hidden');
   543      chromeViewport.classList.remove('hidden');
   544            
   545      // Point the webview to the running Chrome instance's DevTools frontend
   546      // First get the available tabs, then use the DevTools frontend URL
   547      setupChromeViewport(status.port);
   548            
   549      // Update button states
   550      initializeBtn.disabled = true;
   551      restartBtn.disabled = false;
   552      canvasBtn.disabled = false;
   553            
   554      // Update status text
   555      chromeStatusIndicator.innerHTML = `
   556                <span class="status-icon" style="color: #27ae60;"></span>
   557                <span class="status-text">Chrome: Running (PID: ${status.pid})</span>`;
   558            
   559      cdpStatus.className = 'connection-status connected';
   560      cdpStatus.textContent = `Port ${status.port}`;
   561      debugPortDisplay.textContent = status.port;
   562            
   563    } else {
   564      // Chrome is NOT running - HIDE the viewport, SHOW the placeholder
   565      chromePlaceholder.classList.remove('hidden');
   566      chromeViewport.classList.add('hidden');
   567      // Only clear the source if it's not already blank to prevent repeated errors
   568      if (chromeViewport.src !== 'about:blank' && chromeViewport.src !== '') {
   569        chromeViewport.src = 'about:blank'; // Clear the source only once
   570      }
   571            
   572      // Update button states
   573      initializeBtn.disabled = false;
   574      restartBtn.disabled = true;
   575      canvasBtn.disabled = true;
   576
   577      // Update status text
   578      chromeStatusIndicator.innerHTML = `
   579                <span class="status-icon" style="color: #e74c3c;"></span>
   580                <span class="status-text">${status.error ? 'Error' : 'Not Running'}</span>`;
   581
   582      cdpStatus.className = 'connection-status disconnected';
   583      cdpStatus.textContent = 'Disconnected';
   584      debugPortDisplay.textContent = '--';
   585    }
   586        
   587    if (memoryDisplay && status.memory) {
   588      memoryDisplay.textContent = `${status.memory} MB`;
   589    }
   590  }
   591
   592  // --- 3. HELPER FUNCTIONS ---
   593  function displayMessage(message) {
   594    if (!queuedMessagesContainer) return;
