   683  // Function to listen for webview console errors
   684  function setupWebviewListeners() {
   685    if (!chromeViewport) return;
   686
   687    // Listen for crashes in the webview's renderer process
   688    chromeViewport.addEventListener('crashed', (event) => {
   689      console.error(' Webview has crashed!', event);
   690      if (window.electronAPI && window.electronAPI.reportWebviewError) {
   691        window.electronAPI.reportWebviewError({
   692          source: 'WebView Crash',
   693          message: 'The DevTools webview process has crashed.',
   694        });
   695      }
   696    });
   697
   698    // Listen for navigation failures with enhanced error handling
   699    chromeViewport.addEventListener('did-fail-load', (event) => {
   700      // Ignore user-cancelled events (error code -3) for about:blank
   701      if (event.errorCode === -3 && event.validatedURL === 'about:blank') {
   702        console.warn(' WebView navigation to about:blank aborted (likely intentional)');
   703        return;
   704      }
   705            
   706      // Log other navigation failures
   707      if (event.errorCode !== -3) {
   708        console.error(' Webview failed to load:', event);
   709        if (window.electronAPI && window.electronAPI.reportWebviewError) {
   710          window.electronAPI.reportWebviewError({
   711            source: 'WebView Navigation',
   712            message: `Failed to load URL: ${event.validatedURL}. Error code: ${event.errorCode}`,
   713          });
   714        }
   715      }
   716    });
   717
   718    // Listen for console messages inside the webview
   719    chromeViewport.addEventListener('console-message', (e) => {
   720      console.log('Webview Console:', e.message);
   721
   722      // Filter for errors (level 3) and report them to the backend
   723      if (e.level === 3) {
   724        let errorMessage = e.message;
   725                
   726        // Handle specific pageAgent error with rate limiting
   727        if (errorMessage.includes('pageAgent') || errorMessage.includes('Cannot read properties of undefined')) {
   728          console.log(' pageAgent error detected - logging without auto-reload to prevent terminal refresh');
   729                    
   730          // Rate limit: only attempt reload if it's been more than 30 seconds since last attempt
   731          const now = Date.now();
   732          if (!window.lastPageAgentReload || (now - window.lastPageAgentReload) > 30000) {
   733            console.log(' Attempting minimal DevTools refresh after 30s cooldown...');
   734            window.lastPageAgentReload = now;
   735                        
   736            // Use a gentler approach - just refresh the webview source instead of full reload
   737            setTimeout(() => {
   738              if (chromeViewport && chromeViewport.src && !chromeViewport.src.includes('about:blank')) {
   739                const currentSrc = chromeViewport.src;
   740                // Minimal refresh by re-setting the same URL
   741                chromeViewport.src = currentSrc;
   742              }
   743            }, 2000); // Longer delay to reduce disruption
   744          } else {
   745            console.log(' PageAgent reload skipped - within cooldown period');
   746          }
   747                    
   748          errorMessage = `pageAgent error (monitored, minimal impact): ${errorMessage}`;
   749        }
   750                
   751        const error = {
   752          source: 'DevTools Viewport',
   753          message: errorMessage,
   754        };
   755        if (window.electronAPI && window.electronAPI.reportWebviewError) {
   756          window.electronAPI.reportWebviewError(error);
   757        }
   758      }
   759    });
   760
   761    console.log(' Enhanced webview error listeners attached.');
   762  }
