  1509  // **STABLE ERROR MONITORING SYSTEM**
  1510  console.log(' Setting up stable error monitoring system...');
  1511    
  1512  const errorCount = { total: 0, chrome: 0, app: 0 };
  1513  let errorBuffer = []; // Buffer to prevent spam
  1514  const _lastErrorTime = 0;
  1515  const ERROR_THROTTLE_MS = 1000; // Max 1 error per second per type
  1516  const MAX_ERRORS_PER_MINUTE = 10; // Prevent spam
  1517  let errorTimestamps = [];
  1518    
  1519  // Throttled error logging to prevent infinite loops and spam
  1520  function throttledAddError(error) {
  1521    const now = Date.now();
  1522        
  1523    // Remove old timestamps (older than 1 minute)
  1524    errorTimestamps = errorTimestamps.filter(t => now - t < 60000);
  1525        
  1526    // Check rate limit
  1527    if (errorTimestamps.length >= MAX_ERRORS_PER_MINUTE) {
  1528      return; // Drop error to prevent spam
  1529    }
  1530        
  1531    // Check for duplicate recent errors
  1532    const recentSimilar = errorBuffer.find(e => 
  1533      e.message === error.message && 
  1534            e.source === error.source && 
  1535            (now - e.timestamp) < ERROR_THROTTLE_MS,
  1536    );
  1537        
  1538    if (recentSimilar) {
  1539      return; // Drop duplicate
  1540    }
  1541        
  1542    // Add to buffer and clean old entries
  1543    errorBuffer.push(error);
  1544    errorBuffer = errorBuffer.filter(e => now - e.timestamp < 5000); // Keep 5 seconds
  1545    errorTimestamps.push(now);
  1546        
  1547    // Safe error logging
  1548    try {
  1549      addErrorLogEntry(error);
  1550            
  1551      if (error.source.includes('Chrome') || error.source.includes('WebSocket') || error.source.includes('Network')) {
  1552        errorCount.chrome++;
  1553      } else {
  1554        errorCount.app++;
  1555      }
  1556      errorCount.total++;
  1557      updateErrorCounts(errorCount.total, errorCount.chrome, errorCount.app);
  1558    } catch (e) {
  1559      // Prevent infinite loops if error logging itself fails
  1560      console.warn('Error logging failed:', e.message);
  1561    }
  1562  }
  1563    
  1564  // Safe console error capture (prevents infinite loops)
  1565  function captureConsoleErrors() {
  1566    const originalConsoleError = console.error;
  1567    const originalConsoleWarn = console.warn;
  1568        
  1569    let isLoggingError = false; // Prevent recursion
  1570        
  1571    console.error = function(...args) {
  1572      if (!isLoggingError) {
  1573        isLoggingError = true;
  1574        try {
  1575          const errorMessage = args.join(' ');
  1576          // Skip our own error logging messages to prevent loops
  1577          if (!errorMessage.includes('addErrorLogEntry') && !errorMessage.includes('Error logging failed')) {
  1578            throttledAddError({
  1579              timestamp: Date.now(),
  1580              message: errorMessage.substring(0, 200), // Limit message length
  1581              source: 'App Errors',
  1582              type: 'error',
  1583            });
  1584          }
  1585        } finally {
  1586          isLoggingError = false;
  1587        }
  1588      }
  1589      originalConsoleError.apply(console, args);
  1590    };
  1591        
  1592    console.warn = function(...args) {
  1593      if (!isLoggingError) {
  1594        isLoggingError = true;
  1595        try {
  1596          const warningMessage = args.join(' ');
  1597          if (!warningMessage.includes('addErrorLogEntry') && !warningMessage.includes('Error logging failed')) {
  1598            throttledAddError({
  1599              timestamp: Date.now(),
  1600              message: warningMessage.substring(0, 200),
  1601              source: 'Warnings',
  1602              type: 'warning',
  1603            });
  1604          }
  1605        } finally {
  1606          isLoggingError = false;
  1607        }
  1608      }
  1609      originalConsoleWarn.apply(console, args);
  1610    };
  1611  }
