   845→  // --- 5. DRAG AND DROP HELPER FUNCTIONS ---
   846→  function setupTerminalDragAndDrop(container, terminal) {
   847→    console.log('🎯 Setting up drag-and-drop for terminal...');
   848→        
   849→    // Visual feedback styles
   850→    const dropZoneClass = 'terminal-drop-zone';
   851→    const style = document.createElement('style');
   852→    style.textContent = `
   853→            .${dropZoneClass} {
   854→                position: relative;
   855→            }
   856→            .${dropZoneClass}::after {
   857→                content: "📁 Drop files here to insert paths";
   858→                position: absolute;
   859→                top: 0;
   860→                left: 0;
   861→                right: 0;
   862→                bottom: 0;
   863→                background: rgba(52, 152, 219, 0.9);
   864→                color: white;
   865→                display: flex;
   866→                align-items: center;
   867→                justify-content: center;
   868→                font-family: monospace;
   869→                font-size: 16px;
   870→                font-weight: bold;
   871→                z-index: 1000;
   872→                pointer-events: none;
   873→                border: 3px dashed #fff;
   874→                border-radius: 4px;
   875→            }
   876→        `;
   877→    document.head.appendChild(style);
   878→
   879→    // Prevent default drag behaviors on the container
   880→    ['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
   881→      container.addEventListener(eventName, preventDefaults, false);
   882→    });
   883→
   884→    function preventDefaults(e) {
   885→      e.preventDefault();
   886→      e.stopPropagation();
   887→    }
   888→
   889→    // Highlight drop zone when item is dragged over it
   890→    ['dragenter', 'dragover'].forEach(eventName => {
   891→      container.addEventListener(eventName, highlight, false);
   892→    });
   893→
   894→    ['dragleave', 'drop'].forEach(eventName => {
   895→      container.addEventListener(eventName, unhighlight, false);
   896→    });
   897→
   898→    function highlight() {
   899→      container.classList.add(dropZoneClass);
   900→    }
   901→
   902→    function unhighlight() {
   903→      container.classList.remove(dropZoneClass);
   904→    }
   905→
   906→    // Handle dropped files
   907→    container.addEventListener('drop', handleDrop, false);
   908→
   909→    function handleDrop(e) {
   910→      const dt = e.dataTransfer;
   911→      const files = dt.files;
   912→
   913→      handleFiles(files, terminal);
   914→    }
   915→
   916→    function handleFiles(files, terminal) {
   917→      if (!files.length) return;
   918→
   919→      console.log(`📁 Processing ${files.length} dropped file(s)`);
   920→            
   921→      // Process each file
   922→      Array.from(files).forEach((file, index) => {
   923→        const filePath = file.path || file.name;
   924→        console.log(`📂 File ${index + 1}: ${filePath}`);
   925→                
   926→        // Insert the file path into the terminal with proper escaping
   927→        const escapedPath = escapeShellPath(filePath);
   928→                
   929→        // Add a space before if not at the start of line, and space after
   930→        const pathToInsert = ` "${escapedPath}" `;
   931→                
   932→        // Write to terminal (this will appear at cursor position)
   933→        terminal.write(pathToInsert);
   934→                
   935→        // Also send to backend PTY so it's part of the actual command
   936→        window.electronAPI.writeToTerminal(pathToInsert);
   937→      });
   938→
   939→      // Show success feedback
   940→      showDropFeedback(files.length);
   941→    }
   942→
   943→    function escapeShellPath(path) {
   944→      // Escape special characters for shell safety
   945→      return path.replace(/(["\s'$`\\])/g, '\\$1');
   946→    }
   947→
   948→    function showDropFeedback(fileCount) {
   949→      // Create temporary feedback message
   950→      const feedback = document.createElement('div');
   951→      feedback.style.cssText = `
   952→                position: fixed;
   953→                top: 20px;
   954→                right: 20px;
   955→                background: #27ae60;
   956→                color: white;
   957→                padding: 10px 20px;
   958→                border-radius: 4px;
   959→                font-family: monospace;
   960→                font-size: 14px;
   961→                z-index: 10000;
   962→                box-shadow: 0 2px 8px rgba(0,0,0,0.3);
   963→                transition: opacity 0.3s ease;
   964→            `;
   965→      feedback.textContent = `✅ Inserted ${fileCount} file path${fileCount > 1 ? 's' : ''}`;
   966→            
   967→      document.body.appendChild(feedback);
   968→            
   969→      // Fade out and remove after 3 seconds
   970→      setTimeout(() => {
   971→        feedback.style.opacity = '0';
   972→        setTimeout(() => {
   973→          if (feedback.parentNode) {
   974→            feedback.parentNode.removeChild(feedback);
   975→          }
   976→        }, 300);
   977→      }, 3000);
   978→    }
   979→
   980→    console.log('✅ Terminal drag-and-drop setup complete');
   981→  }
