  1613→  // Efficient WebSocket error monitoring
  1614→  function setupWebSocketErrorMonitoring() {
  1615→    if (!window.WebSocket) return; // Skip if WebSocket not available
  1616→        
  1617→    const originalWebSocket = window.WebSocket;
  1618→    window.WebSocket = function(url, protocols) {
  1619→      const ws = new originalWebSocket(url, protocols);
  1620→            
  1621→      // Only log significant WebSocket errors, not every connection attempt
  1622→      ws.addEventListener('error', (_event) => {
  1623→        throttledAddError({
  1624→          timestamp: Date.now(),
  1625→          message: `WebSocket error: ${url.substring(0, 50)}...`,
  1626→          source: 'WebSocket',
  1627→          type: 'error',
  1628→        });
  1629→      });
  1630→            
  1631→      ws.addEventListener('close', (event) => {
  1632→        // Only log abnormal closures
  1633→        if (event.code !== 1000 && event.code !== 1001) {
  1634→          throttledAddError({
  1635→            timestamp: Date.now(),
  1636→            message: `WebSocket closed abnormally: Code ${event.code}`,
  1637→            source: 'WebSocket',
  1638→            type: 'warning',
  1639→          });
  1640→        }
  1641→      });
  1642→            
  1643→      return ws;
  1644→    };
  1645→  }
