  1647→  // Chrome webview error monitoring
  1648→  function setupWebviewErrorMonitoring() {
  1649→    const chromeViewport = document.getElementById('chrome-viewport');
  1650→    if (chromeViewport) {
  1651→      chromeViewport.addEventListener('consolemessage', (event) => {
  1652→        if (event.level >= 2) { // Error level
  1653→          const error = {
  1654→            timestamp: Date.now(),
  1655→            message: event.message,
  1656→            source: 'Chrome Errors',
  1657→            type: event.level === 2 ? 'warning' : 'error',
  1658→          };
  1659→          addErrorLogEntry(error);
  1660→          errorCount.chrome++;
  1661→          errorCount.total++;
  1662→          updateErrorCounts(errorCount.total, errorCount.chrome, errorCount.app);
  1663→        }
  1664→      });
  1665→    }
  1666→  }
