  1668→  // Efficient global error handlers
  1669→  window.addEventListener('error', (event) => {
  1670→    // Skip script loading errors and resource errors that are not critical
  1671→    if (event.filename && event.filename.includes('chrome-extension://')) return;
  1672→        
  1673→    throttledAddError({
  1674→      timestamp: Date.now(),
  1675→      message: `${event.error?.message || event.message} at ${event.filename}:${event.lineno}`.substring(0, 150),
  1676→      source: 'JavaScript Error',
