   211→  // Restore terminal state from persistent storage
   212→  async restoreTerminalState() {
   213→    try {
   214→      console.log('📂 Attempting to restore terminal state...');
   215→            
   216→      const fs = require('fs');
   217→      const path = require('path');
   218→      const stateFile = path.join(__dirname, 'terminal-state.json');
   219→            
   220→      if (!fs.existsSync(stateFile)) {
   221→        console.log('ℹ️ No previous terminal state found, starting fresh');
   222→        return false;
   223→      }
   224→            
   225→      const stateData = await fs.promises.readFile(stateFile, 'utf8');
   226→      const savedState = JSON.parse(stateData);
   227→            
   228→      // Validate state data
   229→      if (!savedState || typeof savedState !== 'object') {
   230→        console.warn('⚠️ Invalid terminal state data, starting fresh');
   231→        return false;
   232→      }
   233→            
   234→      // Check if state is not too old (more than 24 hours)
   235→      const stateAge = Date.now() - new Date(savedState.savedAt).getTime();
   236→      if (stateAge > 24 * 60 * 60 * 1000) {
   237→        console.log('⏰ Terminal state is too old (>24h), starting fresh');
   238→        // Clean up old state file
   239→        fs.unlinkSync(stateFile);
   240→        return false;
   241→      }
   242→            
   243→      // Restore state
   244→      this.terminalState = {
   245→        ...this.terminalState,
   246→        ...savedState,
   247→        pid: null, // Don't restore PID as process is dead
   248→      };
   249→            
   250→      console.log(`✅ Terminal state restored from ${savedState.savedAt}`);
   251→      console.log(`📁 Restored CWD: ${this.terminalState.cwd}`);
   252→            
   253→      return true;
   254→    } catch (_error) {
   255→      console.error('❌ Failed to restore terminal state:', _error);
   256→      return false;
   257→    }
   258→  }
