   402→  // Wait for Chrome to be ready before accepting connections
   403→  async waitForChromeReady(port, timeout = 10000) {
   404→    const startTime = Date.now();
   405→    while (Date.now() - startTime < timeout) {
   406→      try {
   407→        const response = await fetch(`http://localhost:${port}/json/version`);
   408→        if (response.ok) {
   409→          console.log(`✅ Chrome on port ${port} is ready.`);
   410→          return true;
   411→        }
   412→      } catch {
   413→        // Ignore connection errors and retry
   414→      }
   415→      // Wait 200ms before trying again
   416→      await new Promise(resolve => setTimeout(resolve, 200));
   417→    }
   418→    throw new Error(`Chrome on port ${port} did not become ready in time.`);
   419→  }
