   421→  async connectToChrome(port) {
   422→    if (this.cdpClient) {
   423→      try {
   424→        await this.cdpClient.close();
   425→      } catch {
   426→        // Ignore errors closing old client
   427→      }
   428→      this.cdpClient = null;
   429→    }
   430→
   431→    try {
   432→      console.log(`🔗 Connecting to Chrome on port ${port} using CDP...`);
   433→            
   434→      // Get available targets first
   435→      const targets = await CDP.List({ port });
   436→      console.log(`📋 Found ${targets.length} Chrome targets`);
   437→            
   438→      // Find the first page target (not extension, service worker, etc.)
   439→      const pageTarget = targets.find(target => target.type === 'page') || targets[0];
   440→            
   441→      if (!pageTarget) {
   442→        throw new Error('No suitable Chrome target found');
   443→      }
   444→            
   445→      console.log(`🎯 Connecting to target: ${pageTarget.title || pageTarget.url}`);
   446→            
   447→      // Connect to the specific target
   448→      this.cdpClient = await CDP({ port, target: pageTarget });
   449→
   450→      // Enable all necessary domains for DevTools compatibility
   451→      console.log('🔧 Enabling Chrome DevTools Protocol domains...');
   452→      await Promise.all([
   453→        this.cdpClient.Page.enable(),
   454→        this.cdpClient.Runtime.enable(),
   455→        this.cdpClient.DOM.enable(),
   456→        this.cdpClient.CSS.enable(),
   457→        this.cdpClient.Network.enable(),
   458→        this.cdpClient.Debugger.enable(),
   459→        this.cdpClient.Target.setDiscoverTargets({ discover: true }),
   460→      ]).catch(error => {
   461→        console.warn('⚠️ Some CDP domains failed to enable:', error.message);
   462→        // Continue anyway - some domains might not be available
   463→      });
   464→
   465→      // Listen for disconnection events
   466→      this.cdpClient.on('disconnect', () => {
   467→        console.log('🔴 CDP client disconnected.');
   468→        this.cdpClient = null;
   469→        this.updateRendererStatus();
   470→      });
   471→
   472→      // **ADD THIS BLOCK TO LISTEN FOR BACKEND ERRORS**
   473→      this.cdpClient.on('error', (err) => {
   474→        console.error('🔴 CDP Client Error:', err);
   475→        const errorObject = {
   476→          source: 'CDP Backend',
   477→          message: err.message,
   478→          timestamp: new Date().toISOString(),
   479→          type: 'error',
   480→        };
   481→        if (this.mainWindow && !this.mainWindow.isDestroyed()) {
   482→          this.mainWindow.webContents.send('new-error-log', errorObject);
   483→        }
   484→      });
   485→
   486→      console.log('✅ CDP connection successful.');
   487→      return true;
   488→    } catch (_error) {
   489→      console.error('❌ CDP connection failed:', _error);
   490→      this.cdpClient = null;
   491→      return false;
   492→    }
   493→  }
