   495→  async initializeChrome() {
   496→    // **ANTI-GHOSTING FIX**: Always kill any lingering instances before starting.
   497→    console.log('🧹 Checking for and killing any lingering Chrome instances...');
   498→    if (this.chromeInstances && this.chromeInstances.length > 0) {
   499→      const instancesToKill = [...this.chromeInstances];
   500→      for (const instance of instancesToKill) {
   501→        await this.chromeLauncher.killChromeInstance(instance);
   502→      }
   503→      await new Promise(resolve => setTimeout(resolve, 500)); // Give processes time to die
   504→      this.chromeInstances = [];
   505→      this.chromeInstance = null;
   506→      console.log('✅ Lingering instances cleaned up.');
   507→    }
   508→
   509→    try {
   510→      console.log('🚀 Initializing a new Chrome instance...');
   511→      if (!this.chromeInstances) this.chromeInstances = [];
   512→
   513→      const chromeResult = await this.chromeLauncher.launchChrome();
   514→      if (!chromeResult.success) throw new Error(chromeResult.error || 'Failed to launch Chrome');
   515→
   516→      const newInstance = chromeResult.chromeInstance;
   517→      await this.waitForChromeReady(newInstance.port);
   518→
   519→      this.chromeInstances.push(newInstance);
   520→      if (!this.chromeInstance) this.chromeInstance = newInstance;
   521→      if (!this.connectionPool.isInitialized()) await this.connectionPool.initialize(this.chromeInstance.port);
   522→            
   523→      // Establish CDP connection for stable WebSocket communication
   524→      await this.connectToChrome(newInstance.port);
   525→
   526→      console.log(`✅ Chrome instance #${this.chromeInstances.length} launched on port: ${newInstance.port} (PID: ${newInstance.pid})`);
   527→            
   528→      newInstance.process.on('exit', (code) => {
   529→        console.log(`🔴 Chrome instance with PID ${newInstance.pid} exited with code: ${code}.`);
   530→        this.chromeInstances = this.chromeInstances.filter(inst => inst.pid !== newInstance.pid);
   531→        if (this.chromeInstance && this.chromeInstance.pid === newInstance.pid) {
   532→          this.chromeInstance = this.chromeInstances[0] || null;
   533→        }
   534→        this.updateRendererStatus();
   535→      });
   536→
   537→      this.updateRendererStatus();
   538→      return { success: true };
   539→    } catch (_error) {
   540→      console.error('❌ Failed to initialize Chrome:', _error);
   541→      this.updateRendererStatus({ error: _error.message });
   542→      return { success: false, error: _error.message };
   543→    }
   544→  }
