  1311→      // Periodically save terminal state (every 30 seconds when active)
  1312→      if (this.terminalStateInterval) {
  1313→        clearInterval(this.terminalStateInterval);
  1314→      }
  1315→            
  1316→      this.terminalStateInterval = setInterval(async () => {
  1317→        this.updateClaudeContext(); // Update Claude context
  1318→        await this.saveTerminalState(); // Save state including Claude data
  1319→      }, 30000); // Save every 30 seconds
  1320→    } catch (_error) {
