  1322→    }
  1323→  }
  1324→
  1325→  // Update terminal state based on terminal output/input
  1326→  updateTerminalState(data) {
  1327→    try {
  1328→      // Simple command detection - look for command prompts and commands
  1329→      const dataStr = data.toString();
  1330→            
  1331→      // Update current working directory if we detect a cd command response
  1332→      if (dataStr.includes('$') || dataStr.includes('#') || dataStr.includes('>')) {
  1333→        // This might be a prompt, try to extract CWD if possible
  1334→        const cwdMatch = dataStr.match(/(\/.+?)\$/);
  1335→        if (cwdMatch && cwdMatch[1]) {
  1336→          this.terminalState.cwd = cwdMatch[1];
  1337→        }
  1338→      }
  1339→            
  1340→      // Track command history (basic)
  1341→      if (dataStr.includes('\r\n') && dataStr.trim().length > 0) {
  1342→        const lines = dataStr.split('\r\n').filter(line => line.trim().length > 0);
  1343→        lines.forEach(line => {
  1344→          // Only track what looks like actual commands (avoid noise)
  1345→          if (line.match(/^[a-zA-Z0-9]/)) {
  1346→            this.terminalState.history.push({
  1347→              command: line.trim(),
  1348→              timestamp: Date.now(),
  1349→            });
  1350→                        
  1351→            // Keep only last 100 commands to prevent memory bloat
  1352→            if (this.terminalState.history.length > 100) {
  1353→              this.terminalState.history = this.terminalState.history.slice(-100);
  1354→            }
  1355→          }
  1356→        });
  1357→      }
  1358→    } catch {
  1359→      // Silently ignore state update errors to prevent terminal disruption
  1360→    }
  1361→  }
  1362→
  1363→  // Live metrics gathering function
  1364→  async gatherMetrics() {
  1365→    let cpuUsage = 0;
  1366→    try {
  1367→      // app.getCPUUsage() may not be available in all Electron versions
  1368→      if (typeof app.getCPUUsage === 'function') {
  1369→        cpuUsage = app.getCPUUsage().percentCPUUsage;
  1370→      } else {
  1371→        // Fallback: use process CPU usage estimation
  1372→        cpuUsage = process.cpuUsage().user / 1000000; // Convert to percentage approximation
  1373→      }
  1374→    } catch {
  1375→      cpuUsage = 0; // Fallback to 0 if unable to get CPU usage
  1376→    }
  1377→
  1378→    const metrics = {
  1379→      cpu: cpuUsage.toFixed(1),
  1380→      memory: (process.memoryUsage().heapUsed / 1024 / 1024).toFixed(1),
  1381→      chromeTabs: 0,
  1382→      connections: this.connectionPool.getActiveConnectionCount(),
  1383→    };
  1384→
  1385→    if (this.cdpClient) {
  1386→      try {
  1387→        // Use CDP to get tab information instead of fetch
  1388→        const targets = await this.cdpClient.Target.getTargets();
  1389→        // Count page targets (not service workers, etc.)
  1390→        metrics.chromeTabs = targets.targetInfos.filter(target => 
  1391→          target.type === 'page' && !target.url.startsWith('devtools://'),
  1392→        ).length;
  1393→      } catch {
  1394→        // Ignore errors if Chrome is closing or not responsive
  1395→        metrics.chromeTabs = 0;
  1396→      }
  1397→    }
  1398→        
  1399→    // Update Claude context with fresh performance metrics
  1400→    if (this.terminalState.claude) {
  1401→      this.terminalState.claude.appState.performance.cpu = parseFloat(metrics.cpu);
  1402→      this.terminalState.claude.appState.performance.chromeTabs = metrics.chromeTabs;
  1403→      this.terminalState.claude.appState.performance.memory = parseFloat(metrics.memory);
  1404→      this.terminalState.claude.lastUpdate = Date.now();
  1405→    }
  1406→        
  1407→    return metrics;
  1408→  }
  1409→
  1410→  // Centralized status updater - single source of truth for UI updates
  1411→  async updateRendererStatus() {
  1412→    if (this.mainWindow && !this.mainWindow.isDestroyed()) {
  1413→      const isRunning = this.chromeInstances && this.chromeInstances.length > 0;
  1414→      const metrics = await this.gatherMetrics(); // Gather fresh metrics
  1415→
  1416→      const status = {
  1417→        running: isRunning,
  1418→        pid: this.chromeInstance?.pid || null,
  1419→        port: this.chromeInstance?.port || null,
  1420→        webview: true,
  1421→        instanceCount: this.chromeInstances?.length || 0,
  1422→        error: null,
  1423→        ...metrics, // Add all the live metrics to the status object
  1424→      };
  1425→            
  1426→      safeLog('📡 Sending chrome-status with live metrics:', status);
  1427→      this.mainWindow.webContents.send('chrome-status', status);
  1428→    }
  1429→  }
  1430→
  1431→  // Forward Chrome stderr errors to the renderer's error log
  1432→  forwardChromeError(error) {
  1433→    console.error(`❌ Chrome Error: ${error.message}`);
  1434→        
  1435→    // Send the error to the renderer for display in the error log
  1436→    if (this.mainWindow && !this.mainWindow.isDestroyed()) {
  1437→      this.mainWindow.webContents.send('new-error-log', error);
  1438→    }
  1439→  }
  1440→
  1441→  async cleanup() {
  1442→    console.log('🧹 Cleaning up Chrome container...');
  1443→        
  1444→    // Save terminal state before cleanup
  1445→    await this.saveTerminalState();
  1446→        
  1447→    clearInterval(this.metricsInterval); // Clear live metrics timer
  1448→    clearInterval(this.terminalStateInterval); // Clear terminal state timer
  1449→        
  1450→    if (this.cdpClient) {
  1451→      try {
  1452→        await this.cdpClient.close();
  1453→      } catch {
  1454→        // Ignore errors closing CDP client
  1455→      }
  1456→      this.cdpClient = null;
  1457→    }
  1458→        
  1459→    if (this.connectionPool) {
  1460→      await this.connectionPool.cleanup();
  1461→    }
  1462→        
  1463→    if (this.chromeInstance) {
  1464→      await this.chromeLauncher.killChrome();
  1465→    }
  1466→
  1467→    if (this.ptyProcess) {
  1468→      // Save final terminal state before killing process
  1469→      await this.saveTerminalState();
  1470→      this.ptyProcess.kill();
  1471→      this.ptyProcess = null;
  1472→    }
  1473→        
  1474→    this.mainWindow = null;
  1475→        
  1476→    // Don't quit the app - let it stay running as a container
  1477→    console.log('✅ Container cleanup complete with state preservation, app continues running...');
  1478→  }
  1479→}
