          const x = operation.x;
          const y = operation.y;
          const duration = operation.duration || 1000;
          if (this.mainWindow && !this.mainWindow.isDestroyed()) {
            this.mainWindow.webContents.send('ai-cursor-command', {
              action: 'move',
              x: x,
              y: y,
              duration: duration,
            });
          }
          break;
        }

        case 'cursor-click': {
          const clickX = operation.x;
          if (this.mainWindow && !this.mainWindow.isDestroyed()) {
            this.mainWindow.webContents.send('ai-cursor-command', {
              action: 'click',
              x: clickX,
              y: operation.y,
            });
          }
          break;
        }

        case 'cursor-type': {
          const text = operation.text;
          if (this.mainWindow && !this.mainWindow.isDestroyed()) {
            this.mainWindow.webContents.send('ai-cursor-command', {
              action: 'type',
              text: text,
              x: operation.x,
              y: operation.y,
            });
          }
          break;
        }

        case 'navigate-to': {
          // Use CDP to navigate Chrome
          const url = operation.url;
          if (this.cdpClient) {
            await this.cdpClient.Page.navigate({ url: url });
            console.log(`🌐 Claude navigated to: ${url}`);
          }
          break;
        }

        case 'take-screenshot': {
          // Claude can request screenshots
          const screenshot = await this.mainWindow.webContents.capturePage();
          const timestamp = Date.now();
          const screenshotPath = path.join(__dirname, 'tmp', `claude-screenshot-${timestamp}.png`);

          // Ensure tmp directory exists
          const tmpDir = path.dirname(screenshotPath);
          if (!fs.existsSync(tmpDir)) {
