  setupClaudeCodeIntegration() {
    console.log('🤖 Setting up Claude Code CLI integration...');

    // Watch for Claude Code file changes
    const fs = require('fs');
    const watchedPaths = [
      path.join(__dirname, 'renderer'),
      path.join(__dirname, 'chrome-manager'),
      __filename, // main.js itself
    ];

    watchedPaths.forEach(watchPath => {
      if (fs.existsSync(watchPath)) {
        try {
          fs.watch(watchPath, { recursive: true }, (eventType, filename) => {
            if (filename && (filename.endsWith('.js') || filename.endsWith('.html') || filename.endsWith('.css'))) {
              console.log(`🤖 Claude Code detected file change: ${filename}`);

              // Auto-reload renderer if Claude changes UI files
              if (filename.includes('renderer/') && this.mainWindow && !this.mainWindow.isDestroyed()) {
                console.log('🔄 Auto-reloading renderer for Claude Code changes...');
                this.mainWindow.webContents.reload();
              }

              // Broadcast file change to renderer
              if (this.mainWindow && !this.mainWindow.isDestroyed()) {
                this.mainWindow.webContents.send('claude-file-changed', {
                  filename,
                  eventType,
                  timestamp: Date.now(),
                });
              }
            }
          });
          console.log(`👁️ Watching ${watchPath} for Claude Code changes`);
        } catch (_error) {
          console.warn(`⚠️ Could not watch ${watchPath}:`, _error.message);
        }
      }
    });

    // Create Claude Code command interface in terminal
    if (this.ptyProcess) {
      // Add Claude Code specific commands
      this.ptyProcess.write('# Claude Code CLI Integration Active\r');
      this.ptyProcess.write('# Available commands:\r');
      this.ptyProcess.write('# - claude-cursor-move <x> <y>\r');
      this.ptyProcess.write('# - claude-cursor-click <x> <y>\r');
      this.ptyProcess.write('# - claude-take-screenshot\r');
      this.ptyProcess.write('# - claude-app-status\r');
    }
  }

  async setupTerminal() {
    try {
      // Attempt to restore previous terminal state
      await this.restoreTerminalState();

      const shell = os.platform() === 'win32' ? 'powershell.exe' : 'bash';
      const startupCwd = this.terminalState.cwd || __dirname;

      console.log(`🖥️ Starting terminal with CWD: ${startupCwd}`);

      // Update Claude context before starting terminal
      this.updateClaudeContext();

      this.ptyProcess = pty.spawn(shell, [], {
        name: 'xterm-color',
        cols: 80,
        rows: 30,
        cwd: startupCwd, // Use restored CWD or app directory
        env: {
          ...process.env,
          ...this.terminalState.environment, // Restore environment vars with Claude context
          CLAUDE_APP_INTEGRATION: 'true', // Flag for Claude Code CLI
          CLAUDE_APP_PORT: '9223',
        },
      });

      this.ptyProcess.onData(data => {
        // **RACE CONDITION FIX**: Check if the window still exists before sending.
        if (this.mainWindow && !this.mainWindow.isDestroyed()) {
          this.mainWindow.webContents.send('terminal-data', data);
        }

        // Track terminal state changes (simple command history)
        this.updateTerminalState(data);
      });

      // **FIXED RESPAWN LOGIC**: Only respawn on an actual error (non-zero exit code).
      this.ptyProcess.onExit(async ({ exitCode }) => {
        console.log(`🔴 Terminal process exited with code ${exitCode}`);

        // Save state before respawn or exit
        await this.saveTerminalState();

        if (exitCode !== 0) {
          console.log('Respawning terminal due to error...');
          await this.setupTerminal();
        }
      });

      // Update terminal state tracking
      this.terminalState.pid = this.ptyProcess.pid;

      // Restore previous session info if available
      if (this.terminalState.savedAt) {
        const restoreMessage = `# Terminal session restored from ${this.terminalState.savedAt}\r\n`;
        this.ptyProcess.write(restoreMessage);
      }

      // Initialize Claude context in terminal
      const claudeWelcome = '# 🤖 Claude Context Active - All app state available via environment variables\r\n';
      const claudeHelp = '# Type \'env | grep CLAUDE_\' to see all Claude context variables\r\n';
      const claudeCommands = '# Type \'claude-help\' to see all available Claude commands\r\n';
      const sourceCommands = `source ${path.join(__dirname, 'claude-commands.sh')}\r\n`;

      this.ptyProcess.write(claudeWelcome);
      this.ptyProcess.write(claudeHelp);
      this.ptyProcess.write(claudeCommands);
      this.ptyProcess.write('# Loading Claude command suite...\r\n');
      this.ptyProcess.write(sourceCommands);

      this.ptyProcess.write('\r'); // Kickstart the terminal

      // Periodically save terminal state (every 30 seconds when active)
      if (this.terminalStateInterval) {
        clearInterval(this.terminalStateInterval);
      }

      this.terminalStateInterval = setInterval(async () => {
        this.updateClaudeContext(); // Update Claude context
        await this.saveTerminalState(); // Save state including Claude data
      }, 30000); // Save every 30 seconds
    } catch (_error) {
      console.error('❌ PTY process failed to spawn:', _error);
    }
  }

  // Update terminal state based on terminal output/input
  updateTerminalState(data) {
    try {
      // Simple command detection - look for command prompts and commands
      const dataStr = data.toString();

      // Update current working directory if we detect a cd command response
      if (dataStr.includes('$') || dataStr.includes('#') || dataStr.includes('>')) {
        // This might be a prompt, try to extract CWD if possible
        const cwdMatch = dataStr.match(/(\/.+?)\$/);
        if (cwdMatch && cwdMatch[1]) {
          this.terminalState.cwd = cwdMatch[1];
        }
      }

      // Track command history (basic)
      if (dataStr.includes('\r\n') && dataStr.trim().length > 0) {
        const lines = dataStr.split('\r\n').filter(line => line.trim().length > 0);
        lines.forEach(line => {
          // Only track what looks like actual commands (avoid noise)
          if (line.match(/^[a-zA-Z0-9]/)) {
            this.terminalState.history.push({
              command: line.trim(),
              timestamp: Date.now(),
            });

            // Keep only last 100 commands to prevent memory bloat
            if (this.terminalState.history.length > 100) {
              this.terminalState.history = this.terminalState.history.slice(-100);
            }
          }
        });
      }
    } catch {
      // Silently ignore state update errors to prevent terminal disruption
    }
  }

  // Live metrics gathering function
  async gatherMetrics() {
    let cpuUsage = 0;
    try {
      // app.getCPUUsage() may not be available in all Electron versions
      if (typeof app.getCPUUsage === 'function') {
        cpuUsage = app.getCPUUsage().percentCPUUsage;
      } else {
        // Fallback: use process CPU usage estimation
        cpuUsage = process.cpuUsage().user / 1000000; // Convert to percentage approximation
      }
    } catch {
      cpuUsage = 0; // Fallback to 0 if unable to get CPU usage
    }

    const metrics = {
      cpu: cpuUsage.toFixed(1),
      memory: (process.memoryUsage().heapUsed / 1024 / 1024).toFixed(1),
      chromeTabs: 0,
      connections: this.connectionPool.getActiveConnectionCount(),
    };

    if (this.cdpClient) {
      try {
        // Use CDP to get tab information instead of fetch
        const targets = await this.cdpClient.Target.getTargets();
        // Count page targets (not service workers, etc.)
        metrics.chromeTabs = targets.targetInfos.filter(target => 
          target.type === 'page' && !target.url.startsWith('devtools://'),
        ).length;
      } catch {
        // Ignore errors if Chrome is closing or not responsive
        metrics.chromeTabs = 0;
      }
    }

    // Update Claude context with fresh performance metrics
    if (this.terminalState.claude) {
      this.terminalState.claude.appState.performance.cpu = parseFloat(metrics.cpu);
      this.terminalState.claude.appState.performance.chromeTabs = metrics.chromeTabs;
      this.terminalState.claude.appState.performance.memory = parseFloat(metrics.memory);
      this.terminalState.claude.lastUpdate = Date.now();
    }

    return metrics;
  }

  // Centralized status updater - single source of truth for UI updates
  async updateRendererStatus() {
    if (this.mainWindow && !this.mainWindow.isDestroyed()) {
      const isRunning = this.chromeInstances && this.chromeInstances.length > 0;
      const metrics = await this.gatherMetrics(); // Gather fresh metrics

      const status = {
        running: isRunning,
        pid: this.chromeInstance?.pid || null,
        port: this.chromeInstance?.port || null,
        webview: true,
        instanceCount: this.chromeInstances?.length || 0,
        error: null,
        ...metrics, // Add all the live metrics to the status object
      };

      safeLog('📡 Sending chrome-status with live metrics:', status);
      this.mainWindow.webContents.send('chrome-status', status);
    }
  }

  // Forward Chrome stderr errors to the renderer's error log
  forwardChromeError(error) {
    console.error(`❌ Chrome Error: ${error.message}`);

    // Send the error to the renderer for display in the error log
    if (this.mainWindow && !this.mainWindow.isDestroyed()) {
      this.mainWindow.webContents.send('new-error-log', error);
    }
  }

  async cleanup() {
    console.log('🧹 Cleaning up Chrome container...');

    // Save terminal state before cleanup
    await this.saveTerminalState();

    clearInterval(this.metricsInterval); // Clear live metrics timer
    clearInterval(this.terminalStateInterval); // Clear terminal state timer

    if (this.cdpClient) {
      try {
        await this.cdpClient.close();
      } catch {
        // Ignore errors closing CDP client
      }
      this.cdpClient = null;
    }

    if (this.connectionPool) {
      await this.connectionPool.cleanup();
    }

    if (this.chromeInstance) {
      await this.chromeLauncher.killChrome();
    }

    if (this.ptyProcess) {
      // Save final terminal state before killing process
      await this.saveTerminalState();
      this.ptyProcess.kill();
      this.ptyProcess = null;
    }

    this.mainWindow = null;

    // Don't quit the app - let it stay running as a container
    console.log('✅ Container cleanup complete with state preservation, app continues running...');
  }
}

// Create app instance
const containerApp = new SlitherChromeContainer();

// App event handlers
app.whenReady().then(async () => {
  containerApp.setupIPC();
  await containerApp.setupTerminal(); // Now async for state restoration
  containerApp.setupClaudeCodeIntegration(); // Initialize Claude Code CLI integration
  await containerApp.createMainWindow();

  // Start live metrics with reduced frequency to prevent errors
  containerApp.metricsInterval = setInterval(() => {
    containerApp.updateRendererStatus();
  }, 10000); // Reduced from 3 seconds to 10 seconds

  console.log('🚀 Container app ready...');
});

app.on('window-all-closed', () => {
  console.log('🔄 All windows closed, but container app stays running.');
});

app.on('activate', async () => {
  if (BrowserWindow.getAllWindows().length === 0) {
    await containerApp.createMainWindow();
  }
});

// **THE FIX IS HERE**: Perform all cleanup before quitting.
app.on('before-quit', async (event) => {
  console.log('🔄 User requested app shutdown...');
  event.preventDefault(); // Stop the app from quitting immediately

  try {
    await containerApp.cleanup();
    console.log('✅ Cleanup complete. Quitting now.');
  } catch (_err) {
    console.error('❌ Cleanup failed:', _err);
  } finally {
    app.quit(); // Now it's safe to quit
  }
});

app.on('quit', () => {
  // This is the final event. Do not add logs or I/O here.
});

// Export for testing
module.exports = SlitherChromeContainer;
