  // **THE FIX**: Function to completely recreate the webview element
  function recreateWebview() {
    if (!chromeContainer) return;

    // 1. Completely remove the old webview element from the DOM
    if (chromeViewport) {
      chromeViewport.remove();
    }

    // 2. Create a brand new webview element from scratch
    const newWebview = document.createElement('webview');
    newWebview.id = 'chrome-viewport';
    newWebview.className = 'chrome-webview hidden'; // Start hidden
    // Don't set src to prevent immediate loading attempts

    // 3. Append the new, clean webview to the container
    chromeContainer.appendChild(newWebview);

    // 4. Update our global reference to point to the new element
    chromeViewport = newWebview;
    console.log('âœ… Viewport has been fully re-created.');

    // 5. Setup error listeners for the new webview
    setupWebviewListeners();
  }

  // Function to listen for webview console errors
  function setupWebviewListeners() {
    if (!chromeViewport) return;

    // Listen for crashes in the webview's renderer process
    chromeViewport.addEventListener('crashed', (event) => {
      console.error('ğŸ”¥ Webview has crashed!', event);
      if (window.electronAPI && window.electronAPI.reportWebviewError) {
        window.electronAPI.reportWebviewError({
          source: 'WebView Crash',
          message: 'The DevTools webview process has crashed.',
        });
      }
    });

    // Listen for navigation failures with enhanced error handling
    chromeViewport.addEventListener('did-fail-load', (event) => {
      // Ignore user-cancelled events (error code -3) for about:blank
      if (event.errorCode === -3 && event.validatedURL === 'about:blank') {
        console.warn('ğŸ›‘ WebView navigation to about:blank aborted (likely intentional)');
        return;
      }

      // Log other navigation failures
      if (event.errorCode !== -3) {
        console.error('ğŸ”¥ Webview failed to load:', event);
        if (window.electronAPI && window.electronAPI.reportWebviewError) {
          window.electronAPI.reportWebviewError({
            source: 'WebView Navigation',
            message: `Failed to load URL: ${event.validatedURL}. Error code: ${event.errorCode}`,
          });
        }
      }
    });

    // Listen for console messages inside the webview
    chromeViewport.addEventListener('console-message', (e) => {
      console.log('Webview Console:', e.message);

      // Filter for errors (level 3) and report them to the backend
      if (e.level === 3) {
        let errorMessage = e.message;

        // Handle specific pageAgent error with rate limiting
        if (errorMessage.includes('pageAgent') || errorMessage.includes('Cannot read properties of undefined')) {
          console.log('ğŸ”§ pageAgent error detected - logging without auto-reload to prevent terminal refresh');

          // Rate limit: only attempt reload if it's been more than 30 seconds since last attempt
          const now = Date.now();
          if (!window.lastPageAgentReload || (now - window.lastPageAgentReload) > 30000) {
            console.log('ğŸ”„ Attempting minimal DevTools refresh after 30s cooldown...');
            window.lastPageAgentReload = now;

            // Use a gentler approach - just refresh the webview source instead of full reload
            setTimeout(() => {
              if (chromeViewport && chromeViewport.src && !chromeViewport.src.includes('about:blank')) {
                const currentSrc = chromeViewport.src;
                // Minimal refresh by re-setting the same URL
                chromeViewport.src = currentSrc;
              }
            }, 2000); // Longer delay to reduce disruption
          } else {
            console.log('â¸ï¸ PageAgent reload skipped - within cooldown period');
          }

          errorMessage = `pageAgent error (monitored, minimal impact): ${errorMessage}`;
        }

        const error = {
          source: 'DevTools Viewport',
          message: errorMessage,
        };
        if (window.electronAPI && window.electronAPI.reportWebviewError) {
          window.electronAPI.reportWebviewError(error);
        }
      }
    });

    console.log('âœ… Enhanced webview error listeners attached.');
  }

