  // Helper function to detect canvas server
  async function detectCanvasUrl() {
    const commonPorts = [8000, 8001, 8002, 8003, 8004, 8005];

    for (const port of commonPorts) {
      try {
        const response = await fetch(`http://localhost:${port}/canvas.html`, {
          method: 'HEAD',
