ipcMain.handle('claude-file-operation', async (event, operation) => {
  try {
    console.log(`ğŸ¤– Claude Code operation: ${operation.type}`);

    switch (operation.type) {
    case 'cursor-move': {
      // Forward cursor commands to webview
      const x = operation.x;
      const y = operation.y;
      const duration = operation.duration || 1000;
      if (this.mainWindow && !this.mainWindow.isDestroyed()) {
        this.mainWindow.webContents.send('ai-cursor-command', {
          action: 'move',
          x: x,
          y: y,
          duration: duration,
        });
      }
      break;
    }

    case 'cursor-click': {
      const clickX = operation.x;
      if (this.mainWindow && !this.mainWindow.isDestroyed()) {
        this.mainWindow.webContents.send('ai-cursor-command', {
          action: 'click',
          x: clickX,
          y: operation.y,
        });
      }
