// Handle screenshot request from main app
ipcMain.handle('take-screenshot', async (_event) => {
  try {
    const { desktopCapturer } = require('electron');

    // Get available screens
    const sources = await desktopCapturer.getSources({
      types: ['screen'],
      thumbnailSize: { width: 1920, height: 1080 }
    });

    if (sources.length > 0) {
      // Get the primary screen
      const primarySource = sources[0];
      const dataUrl = primarySource.thumbnail.toDataURL();

      console.log('ğŸ“¸ Screenshot captured successfully');

      // Send screenshot to renderer window
      if (this.mainWindow && !this.mainWindow.isDestroyed()) {
        this.mainWindow.webContents.send('screenshot-captured', {
          id: Date.now().toString(),
          timestamp: Date.now(),
          dataUrl: dataUrl
        });
      }

      return { success: true, dataUrl: dataUrl };
    } else {
      throw new Error('No screens available for capture');
    }
  } catch (_error) {
    console.error('âŒ Screenshot error:', _error);
    return { success: false, error: _error.message };
  }
});

// Handle request to open planning tab
ipcMain.handle('open-planning-tab', async (_event) => {
  try {
    console.log('ğŸ“ Opening planning tab...');

    // Send message to renderer to switch to planning tab
    if (this.mainWindow && !this.mainWindow.isDestroyed()) {
      this.mainWindow.webContents.send('open-planning-tab');

      // Focus the window
      this.mainWindow.focus();

      return { success: true };
    } else {
      throw new Error('Main window not available');
    }
  } catch (_error) {
    console.error('âŒ Open planning tab error:', _error);
    return { success: false, error: _error.message };
  }
});

