// preload.js - Fixed version without breaking Xterm imports

const { contextBridge, ipcRenderer } = require('electron');

console.log('ğŸ”§ Starting preload script...');

// Expose APIs to renderer process
contextBridge.exposeInMainWorld('electronAPI', {
  // Xterm will be loaded via CDN in the renderer

  // Status and Connection
  getChromeStatus: () => ipcRenderer.invoke('get-chrome-status'),
  getConnectionStatus: () => ipcRenderer.invoke('get-connection-status'),

  // Chrome Actions
  initializeChrome: () => ipcRenderer.invoke('initialize-chrome'),
  restartChrome: () => ipcRenderer.invoke('restart-chrome'),
  reconnectDevTools: () => {
    console.log('ğŸŒ‰ Preload bridge: reconnectDevTools called, invoking reconnect-devtools');
    return ipcRenderer.invoke('reconnect-devtools');
  },
  navigateCanvas: () => ipcRenderer.invoke('navigate-canvas'),

  // File & Session Management
  importMdFile: () => ipcRenderer.invoke('import-md-file'),
  clearSessions: () => ipcRenderer.invoke('clear-sessions'),
  exportLogs: () => ipcRenderer.invoke('export-logs'),

  // **NEW**: WebSocket Management
  forceKillWebSockets: () => ipcRenderer.invoke('force-kill-websockets'),
  restartWebSocketServer: () => ipcRenderer.invoke('restart-websocket-server'),

  // Real Terminal Streaming
  onTerminalData: (callback) => ipcRenderer.on('terminal-data', (event, data) => callback(data)),
  writeToTerminal: (data) => ipcRenderer.send('terminal-write', data),
  resizeTerminal: (cols, rows) => ipcRenderer.send('terminal-resize', { cols, rows }),

  // Messaging
  queueUserMessage: (message) => ipcRenderer.invoke('queue-user-message', message),
  sendMessage: (messageData) => ipcRenderer.invoke('send-message', messageData),

  // A function to receive messages from the main process
  onChromeStatusUpdate: (callback) => ipcRenderer.on('chrome-status', (event, ...args) => callback(...args)),

  // Error reporting for webview errors
  reportWebviewError: (error) => ipcRenderer.send('webview-error', error),

  // Listen for formatted error logs from backend
  onNewErrorLog: (callback) => {
    console.log('ğŸ”§ Preload: Setting up new-error-log listener');
    return ipcRenderer.on('new-error-log', (event, error) => {
      console.log('ğŸ”§ Preload: Received new-error-log event:', error);
      callback(error);
    });
  },

  // Screenshot and File System APIs
  ensureDir: (dirPath) => ipcRenderer.invoke('ensure-dir', dirPath),
  saveFile: (filePath, data, encoding) => ipcRenderer.invoke('save-file', filePath, data, encoding),
  deleteFile: (filePath) => ipcRenderer.invoke('delete-file', filePath),
  deleteDir: (dirPath) => ipcRenderer.invoke('delete-dir', dirPath),
  captureScreen: () => ipcRenderer.invoke('capture-screen'),
  takeScreenshot: () => ipcRenderer.invoke('take-screenshot'),
  openPlanningTab: () => ipcRenderer.invoke('open-planning-tab'),

  // **CLAUDE CODE CLI INTEGRATION** - Native terminal control
  claudeFileOperation: (operation) => ipcRenderer.invoke('claude-file-operation', operation),
  claudeGetAppState: () => ipcRenderer.invoke('claude-get-app-state'),

  // Listen for Claude-driven cursor commands
  onAiCursorCommand: (callback) => ipcRenderer.on('ai-cursor-command', (event, command) => callback(command)),

  // Listen for Claude file changes
  onClaudeFileChanged: (callback) => ipcRenderer.on('claude-file-changed', (event, changeInfo) => callback(changeInfo)),

  // Test property to verify bridge works
  testConnection: () => 'bridge-working',
});

console.log('âœ… Preload script loaded successfully');

