const headerScreenshotBtn = document.getElementById('header-screenshot-btn');
if (headerScreenshotBtn) {
  headerScreenshotBtn.addEventListener('click', async () => {
    headerScreenshotBtn.disabled = true;
    headerScreenshotBtn.textContent = 'ğŸ“¸ Capturing...';

    try {
      // Take screenshot
      await window.captureScreenshot();

      // Switch to planning tab
      const planningTabBtn = document.querySelector('[data-tab="planning"]');
      const planningTabContent = document.getElementById('planning-tab');

      if (planningTabBtn && planningTabContent) {
        // Remove active class from all tabs
        tabButtons.forEach(btn => btn.classList.remove('active'));
        tabContents.forEach(content => content.classList.remove('active'));

        // Activate planning tab
        planningTabBtn.classList.add('active');
        planningTabContent.classList.add('active');

        console.log('âœ… Switched to planning tab after screenshot');
      }
    } catch (error) {
      console.error('Header screenshot button error:', error);
    } finally {
      headerScreenshotBtn.disabled = false;
      headerScreenshotBtn.textContent = 'ğŸ“¸ Screenshot';
    }
  });
}

