}

.action-item:last-child {
    border-bottom: none;
}

.action-timestamp {
    color: #9b9a97;
    font-size: 0.75rem;
    margin-top: 2px;
}

/* Connection Status Section */
.connection-status-section {
    padding: 20px 16px;
    background: #fff;
    overflow-y: auto;
    max-height: 200px;
}

.connection-status-section .section-header h4 {
    font-size: 0.875rem;
    font-weight: 600;
    color: #37352f;
    margin-bottom: 16px;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.connection-details {
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.connection-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px 12px;
    background: #f7f6f3;
    border: 1px solid #e9e9e7;
    border-radius: 6px;
    transition: all 0.15s ease;
}

.connection-item:hover {
    background: #f1f1ef;
}

.connection-label {
    font-size: 0.8125rem;
    font-weight: 500;
    color: #37352f;
}

.connection-status {
    font-size: 0.75rem;
