    gap: 8px;
}

/* .chrome-container and its states consolidated above for CSS specificity ordering */

.chrome-placeholder {
    position: absolute;
    inset: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    background: linear-gradient(135deg, #f7f6f3 0%, #fff 100%);
}

.placeholder-content {
    text-align: center;
    color: #9b9a97;
}

.loading-spinner {
    width: 32px;
    height: 32px;
    border: 3px solid #e9e9e7;
    border-top: 3px solid #0f70b8;
    border-radius: 50%;
    animation: spin 1s linear infinite;
    margin: 0 auto 16px;
}

@keyframes spin {
    0% { transform: rotate(0deg); }

    100% { transform: rotate(360deg); }
}

.chrome-webview {
    width: 100%;
    height: 100%;
    border-radius: 8px;
    flex: 1;
    border: none;
    background: #fff;
}

.chrome-info {
    margin-top: 12px;
    font-size: 0.875rem;
