    user-select: none;
}

.terminal-input {
    flex: 1;
    background: transparent;
    border: none;
    color: #d4d4d4;
    font-family: inherit;
    font-size: 13px;
    outline: none;
    line-height: 1.4;
}

.terminal-input::placeholder {
    color: #6a9955;
    font-style: italic;
}

.terminal-input:focus {
    background: rgb(255 255 255 / 2%);
    border-radius: 2px;
}

/* VS Code Terminal Suggestions */
.terminal-suggestions {
    position: absolute;
    bottom: 100%;
    left: 16px;
    right: 16px;
    background: #252526;
    border: 1px solid #3e3e42;
    border-radius: 4px;
    max-height: 200px;
    overflow-y: auto;
    z-index: 1000;
    display: none;
}

.suggestion-item {
    padding: 8px 12px;
    color: #ccc;
    cursor: pointer;
    font-size: 13px;
    border-bottom: 1px solid #3e3e42;
}

.suggestion-item:hover,
.suggestion-item.selected {
    background: #094771;
    color: #fff;
}

.suggestion-item:last-child {
    border-bottom: none;
}

.suggestion-description {
    color: #9cdcfe;
    font-size: 11px;
    margin-top: 2px;
}

/* VS Code Style Terminal Tabs */
.terminal-tabs {
    display: flex;
    align-items: center;
    background: #2d2d30;
    border-bottom: 1px solid #3e3e42;
    padding: 0 8px;
    min-height: 35px;
    position: relative;
    overflow-x: auto;
    scrollbar-width: none;
}

.terminal-tabs::-webkit-scrollbar {
    display: none;
}

.terminal-tab {
    display: flex;
    align-items: center;
    gap: 6px;
    padding: 6px 12px;
    background: none;
    border: none;
    color: #ccc;
    cursor: pointer;
    border-bottom: 2px solid transparent;
    white-space: nowrap;
    font-size: 13px;
    min-width: max-content;
    position: relative;
    transition: all 0.15s ease;
}

.terminal-tab:hover:not(.active) {
    background: #37373d;
    color: #fff;
}

.terminal-tab.active {
    color: #fff;
    background: #1e1e1e;
    border-bottom-color: #007acc;
}

.terminal-tab .tab-icon {
    font-size: 12px;
    opacity: 0.8;
}

.terminal-tab .tab-title {
    font-weight: 400;
}

.terminal-tab-controls {
    margin-left: auto;
    display: flex;
    align-items: center;
    gap: 4px;
}

.terminal-control-btn {
    width: 24px;
    height: 24px;
    border: none;
    background: none;
    color: #ccc;
    cursor: pointer;
    border-radius: 3px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 11px;
    transition: all 0.15s ease;
}

.terminal-control-btn:hover {
    background: #37373d;
    color: #fff;
}

/* Terminal Content Areas */
.terminal-content {
    display: none;
    flex-direction: column;
    height: 100%;
    background: #1e1e1e;
}

.terminal-content.active {
    display: flex;
}

/* Enhanced Terminal Controls */
.terminal-status-bar {
    display: flex;
    align-items: center;
    gap: 12px;
    flex: 1;
}

.terminal-info {
    display: flex;
    align-items: center;
    gap: 8px;
    margin-left: auto;
    font-size: 11px;
    color: #999;
}

.terminal-shell {
    background: #007acc;
    color: #fff;
    padding: 2px 6px;
    border-radius: 3px;
    font-weight: 500;
}

.terminal-path {
    font-family: Consolas, 'Courier New', 'SF Mono', Monaco, monospace;
}

/* Debug Console Specific Styles */
.debug-output {
    background: #0e1419;
    border: 1px solid #1e2328;
}

.debug-prompt {
    color: #ff6b35 !important;
    font-weight: bold;
}

.debug-status {
    padding: 2px 6px;
    border-radius: 3px;
    font-size: 11px;
    font-weight: 500;
}

.debug-status.connected {
    background: #4caf50;
    color: #fff;
}

.debug-status.disconnected {
    background: #f44336;
    color: #fff;
}

/* Logs Terminal Specific Styles */
.logs-output {
    font-family: 'SF Mono', Consola, Menlo, Monaco, monospace;
    font-size: 12px;
    line-height: 1.5;
}

.log-level-select {
    background: #3c3c3c;
    border: 1px solid #555;
    color: #ccc;
    padding: 4px 8px;
    border-radius: 3px;
    font-size: 11px;
}

.log-filter {
    background: #007acc;
    color: #fff;
    padding: 2px 6px;
    border-radius: 3px;
    font-size: 11px;
    font-weight: 500;
}

/* Chrome Console Specific Styles */
.chrome-console-output {
    background: #242424;
    border: 1px solid #333;
}

.chrome-prompt {
    color: #61dafb !important;
    font-weight: bold;
}

.chrome-status {
    padding: 2px 6px;
    border-radius: 3px;
    font-size: 11px;
    font-weight: 500;
}

.chrome-status.connected {
    background: #4caf50;
    color: #fff;
}

.chrome-status.disconnected {
    background: #f44336;
    color: #fff;
}

/* Log Entry Styling */
.log-entry {
    display: flex;
    align-items: flex-start;
    gap: 8px;
    padding: 3px 0;
    border-bottom: 1px solid #2a2a2a;
}

.log-timestamp {
    color: #888;
    font-size: 11px;
    min-width: 80px;
    font-family: monospace;
}

.log-level {
    padding: 1px 4px;
    border-radius: 2px;
    font-size: 10px;
    font-weight: 500;
    min-width: 50px;
    text-align: center;
}

.log-level.error {
    background: #f44336;
    color: #fff;
}

.log-level.warn {
    background: #ff9800;
    color: #fff;
}

.log-level.info {
    background: #2196f3;
    color: #fff;
}

.log-level.debug {
    background: #9c27b0;
    color: #fff;
}

.log-message {
    flex: 1;
    color: #d4d4d4;
    word-break: break-all;
}

/* Terminal Maximized State */
.terminal-section.maximized {
    position: fixed !important;
    inset: 0;
    z-index: 9999;
    margin: 0;
    border-radius: 0;
    width: 100vw !important;
    height: 100vh !important;
}

/* Enhanced Terminal Line Types (merged with earlier definitions) */

.terminal-line.info {
    color: #2196f3;
}

.debug-line.command {
    color: #ff6b35;
    font-weight: bold;
}

.debug-line.success {
    color: #4caf50;
}

.chrome-line.command {
    color: #61dafb;
    font-weight: bold;
}

.chrome-line.success {
    color: #4caf50;
}

/* Real Terminal Container */
.real-terminal-container {
    flex: 1;
    width: 100%;
    height: 100%;
    background: #1e1e1e;
    min-height: 300px;
}

/* xterm.js overrides moved up for CSS specificity ordering */

/* Command History Indicator */
.command-history-indicator {
    position: absolute;
    right: 16px;
    top: 50%;
    transform: translateY(-50%);
    color: #6a9955;
    font-size: 11px;
    opacity: 0.7;
}

/* Screenshots Section Styles */
.screenshots-section {
    padding: 15px;
    border-bottom: 1px solid #e9e9e7;
    background: #fff;
}

.screenshots-section .section-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 15px;
}

.screenshot-controls {
    display: flex;
    align-items: center;
    gap: 10px;
}

.screenshot-count {
    font-size: 11px;
    color: #666;
    background: rgb(0 0 0 / 5%);
    padding: 2px 8px;
    border-radius: 12px;
    font-weight: 500;
}

.screenshots-container {
    max-height: 400px;
    overflow-y: auto;
    border: 1px solid #e9e9e7;
    border-radius: 6px;
    background: #fafafa;
}

.no-screenshots {
    text-align: center;
    padding: 40px 20px;
    color: #999;
}

.no-screenshots-icon {
    font-size: 32px;
    display: block;
    margin-bottom: 10px;
    opacity: 0.6;
}

.no-screenshots p {
    margin: 0 0 5px;
    font-size: 14px;
    color: #666;
}

.no-screenshots small {
    font-size: 12px;
    opacity: 0.7;
}

.screenshot-item {
    border-bottom: 1px solid #e9e9e7;
    background: #fff;
    transition: all 0.2s ease;
}

.screenshot-item:hover {
    background: #f8f8f7;
}

.screenshot-item:last-child {
    border-bottom: none;
}

.screenshot-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 12px 15px;
    cursor: pointer;
    user-select: none;
    transition: background-color 0.2s;
}

.screenshot-header:hover {
    background: rgb(0 0 0 / 2%);
}

.screenshot-info {
    display: flex;
    align-items: center;
    gap: 12px;
    font-size: 13px;
}

.screenshot-timestamp {
    color: #666;
    font-family: 'SF Mono', Monaco, monospace;
    font-size: 12px;
    font-weight: 500;
}

.screenshot-size {
    color: #888;
    font-size: 11px;
    background: rgb(0 0 0 / 5%);
    padding: 2px 6px;
    border-radius: 8px;
}

.screenshot-toggle {
    font-size: 12px;
    color: #06c;
    font-weight: 600;
    transition: transform 0.2s ease;
}

.screenshot-toggle.expanded {
    transform: rotate(90deg);
}

.screenshot-content {
    display: none;
    padding: 0 15px 15px;
    animation: slide-down 0.2s ease;
}

.screenshot-content.expanded {
    display: block;
}

@keyframes slide-down {
    from {
        opacity: 0;
        transform: translateY(-10px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.screenshot-image {
    max-width: 100%;
    border-radius: 6px;
    border: 1px solid #e9e9e7;
    box-shadow: 0 2px 12px rgb(0 0 0 / 8%);
    transition: transform 0.2s ease;
}

.screenshot-image:hover {
    transform: scale(1.02);
    box-shadow: 0 4px 20px rgb(0 0 0 / 12%);
}

.screenshot-actions {
    margin-top: 12px;
    display: flex;
    gap: 8px;
}

.screenshot-path {
    margin-bottom: 10px;
    padding: 8px;
    background: rgb(0 0 0 / 3%);
    border-radius: 4px;
    border-left: 3px solid #06c;
}

.screenshot-path code {
    background: rgb(0 0 0 / 10%);
    padding: 2px 6px;
    border-radius: 3px;
    font-family: 'SF Mono', Monaco, monospace;
    font-size: 11px;
    color: #06c;
    cursor: pointer;
    transition: all 0.2s ease;
    user-select: all;
}

.screenshot-path code:hover {
    background: rgb(0 102 204 / 10%);
    color: #049;
}

.screenshot-btn {
    background: #f8f9fa;
    border: 1px solid #dee2e6;
    color: #495057;
    padding: 4px 8px;
    border-radius: 4px;
    font-size: 11px;
    cursor: pointer;
    transition: all 0.2s ease;
    white-space: nowrap;
}

.screenshot-btn:hover {
    background: #e9ecef;
    border-color: #ced4da;
    color: #343a40;
}

.screenshot-btn:active {
    background: #dee2e6;
    transform: translateY(1px);
}

.screenshot-content img {
    max-width: 100%;
    height: auto;
    border-radius: 6px;
    border: 1px solid #e9e9e7;
    box-shadow: 0 2px 12px rgb(0 0 0 / 8%);
    transition: transform 0.2s ease;
    display: block;
}

.screenshot-content img:hover {
    transform: scale(1.02);
    box-shadow: 0 4px 20px rgb(0 102 204 / 15%);
}

.btn-sm {
    padding: 6px 12px;
    font-size: 11px;
    border-radius: 4px;
    font-weight: 500;
}

