/* Modal Styles */
.modal {
    position: fixed;
    inset: 0;
    background: rgb(0 0 0 / 50%);
    backdrop-filter: blur(4px);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
    animation: fade-in 0.2s ease;
}

@keyframes fade-in {
    from { opacity: 0; }

    to { opacity: 1; }
}

.modal-content {
    background: #fff;
    border-radius: 12px;
    width: 85%;
    max-width: 700px;
    max-height: 85%;
    display: flex;
    flex-direction: column;
    box-shadow: 0 25px 50px -12px rgb(0 0 0 / 25%);
    border: 1px solid #e9e9e7;
    animation: slide-up 0.3s ease;
}

@keyframes slide-up {
    from { 
        opacity: 0;
        transform: translateY(20px) scale(0.95);
    }

    to { 
        opacity: 1;
        transform: translateY(0) scale(1);
    }
}

.modal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px 24px;
    border-bottom: 1px solid #e9e9e7;
}

/* .modal-header h3 consolidated above for CSS specificity ordering */

.modal-close {
    background: #f7f6f3;
    border: 1px solid #e9e9e7;
    color: #9b9a97;
    font-size: 1.25rem;
    cursor: pointer;
    padding: 6px 8px;
    border-radius: 6px;
    transition: all 0.15s ease;
    width: 32px;
    height: 32px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.modal-close:hover {
    background: #f1f1ef;
    border-color: #d3d1cb;
    color: #37352f;
}

.modal-body {
    flex: 1;
    padding: 24px;
    overflow-y: auto;
}

.modal-body pre {
    background: #f7f6f3;
    border: 1px solid #e9e9e7;
    border-radius: 8px;
    padding: 20px;
    color: #37352f;
    font-size: 0.8125rem;
    line-height: 1.6;
    white-space: pre-wrap;
    font-family: 'SF Mono', Monaco, 'Cascadia Code', monospace;
