/* Modal Styles */
.modal {
    position: fixed;
    inset: 0;
    background: rgb(0 0 0 / 50%);
    backdrop-filter: blur(4px);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
    animation: fade-in 0.2s ease;
}

@keyframes fade-in {
    from { opacity: 0; }

    to { opacity: 1; }
}

.modal-content {
    background: #fff;
    border-radius: 12px;
    width: 85%;
    max-width: 700px;
    max-height: 85%;
