/* Screenshots Section Styles */
.screenshots-section {
    padding: 15px;
    border-bottom: 1px solid #e9e9e7;
    background: #fff;
}

.screenshots-section .section-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 15px;
}

.screenshot-controls {
    display: flex;
    align-items: center;
    gap: 10px;
}

.screenshot-count {
    font-size: 11px;
    color: #666;
    background: rgb(0 0 0 / 5%);
    padding: 2px 8px;
    border-radius: 12px;
    font-weight: 500;
}

.screenshots-container {
    max-height: 400px;
    overflow-y: auto;
    border: 1px solid #e9e9e7;
