}

.screenshot-item {
    border-bottom: 1px solid #e9e9e7;
    background: #fff;
    transition: all 0.2s ease;
}

.screenshot-item:hover {
    background: #f8f8f7;
}

.screenshot-item:last-child {
    border-bottom: none;
}

.screenshot-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 12px 15px;
    cursor: pointer;
    user-select: none;
    transition: background-color 0.2s;
}

.screenshot-header:hover {
    background: rgb(0 0 0 / 2%);
}

.screenshot-info {
    display: flex;
    align-items: center;
    gap: 12px;
    font-size: 13px;
}

.screenshot-timestamp {
    color: #666;
    font-family: 'SF Mono', Monaco, monospace;
    font-size: 12px;
    font-weight: 500;
}

.screenshot-size {
    color: #888;
    font-size: 11px;
    background: rgb(0 0 0 / 5%);
    padding: 2px 6px;
