    color: #495057;
    padding: 4px 8px;
    border-radius: 4px;
    font-size: 11px;
    cursor: pointer;
    transition: all 0.2s ease;
    white-space: nowrap;
}

.screenshot-btn:hover {
    background: #e9ecef;
    border-color: #ced4da;
    color: #343a40;
}

.screenshot-btn:active {
