    transition: transform 0.2s ease;
    display: block;
}

.screenshot-content img:hover {
    transform: scale(1.02);
    box-shadow: 0 4px 20px rgb(0 102 204 / 15%);
}

.btn-sm {
    padding: 6px 12px;
    font-size: 11px;
    border-radius: 4px;
    font-weight: 500;
}

/* .file-import merged with earlier definition */

/* Dynamic CSS Custom Properties for Notion-level viewport handling */
:root {
    --viewport-width: 100vw;
    --viewport-height: 100vh;
    --content-width: 80vw;
    --content-height: 80vh;
}

/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', system-ui, sans-serif;
    background: linear-gradient(135deg, #f7f6f3 0%, #fff 100%);
    color: #37352f;
    overflow-y: auto;
    overflow-x: hidden;
    font-weight: 400;
    line-height: 1.5;
    width: 100%;
    min-height: 100vh;
}

/* Base chrome container styles */
.chrome-container {
    flex: 1;
    position: relative;
    background: #fafafa;
    border-radius: 8px;
    margin: 16px;
    overflow: hidden;
    box-shadow: 0 2px 8px rgb(0 0 0 / 6%);
    display: flex;
    flex-direction: column;
    min-height: 400px;
    transition: all 0.3s ease;
    resize: both;
    max-width: calc(100vw - 32px);
    max-height: calc(100vh - 200px);
}

.chrome-container:hover {
    box-shadow: 0 4px 16px rgb(0 0 0 / 12%);
}

.chrome-container:focus-within {
    box-shadow: 0 0 0 2px rgb(15 112 184 / 20%);
}

/* Base session container styles */
.session-container {
    background: #fff;
    border: 1px solid #e9e9e7;
    border-radius: 8px;
    padding: 16px;
    max-height: 80px;
    min-height: 60px;
    overflow-y: auto;
    margin-bottom: 20px;
    scrollbar-width: thin;
    scrollbar-color: #d3d1cb transparent;
    transition: all 0.15s ease;
}

.session-container:hover {
    border-color: #d3d1cb;
    box-shadow: 0 2px 8px rgb(0 0 0 / 6%);
}

/* Dynamic layout classes for live content awareness */
body.chrome-active .chrome-container {
    opacity: 1;
    transform: scale(1);
    transition: all 0.3s ease;
}

body.sessions-active .session-container {
    border-left: 3px solid #10b981;
    background: rgb(16 185 129 / 5%);
}

/* Base error log styles */
.error-log {
    height: 100%;
    overflow-y: auto;
    padding: 16px 20px;
}

body.errors-active .error-log {
    border-left: 3px solid #ef4444;
    background: rgb(239 68 68 / 5%);
}

/* Terminal drag-and-drop enhancements */
.terminal-section {
    position: relative;
    transition: all 0.2s ease;
    flex: 1;
    display: flex;
    flex-direction: column;
    background: #2d2d30;
    border-radius: 8px;
    margin: 8px;
    overflow: hidden;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.4);
    min-height: 300px;
    border: 1px solid #3c3c3c;
}

.terminal-section:hover {
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgb(0 0 0 / 10%);
}

/* Drag-and-drop feedback animations */
@keyframes drop-pulse {
    0%, 100% { transform: scale(1); }

    50% { transform: scale(1.02); }
}

.terminal-drop-zone {
    animation: drop-pulse 1s ease-in-out infinite;
}

/* Success feedback animation */
@keyframes success-slide {
    0% {
        transform: translateX(100%);
        opacity: 0;
    }

    10%, 90% {
        transform: translateX(0);
        opacity: 1;
    }

    100% {
        transform: translateX(100%);
        opacity: 0;
    }
}

.drop-success-feedback {
    animation: success-slide 3s ease-in-out;
}

/* Live notification styles */
.notification {
    animation: slide-in-right 0.3s ease-out;
}

@keyframes slide-in-right {
    from {
        transform: translateX(100%);
        opacity: 0;
    }

    to {
        transform: translateX(0);
        opacity: 1;
    }
}

.container {
    display: grid;

    /* Define grid rows: header, main content (takes up remaining space), footer */
    grid-template: "header header header" auto "toolbar viewport sidebar" minmax(600px, 1fr) "footer footer footer" auto / minmax(240px, 280px) 1fr minmax(300px, 350px);

    /* Define grid columns: left toolbar, main viewport, right sidebar */

    /* Name the grid areas for easy placement */
    min-height: 100vh;
    height: auto;
    overflow: visible;
}

/* Header Styles */
.header {
    grid-area: header;
    background: #fff;
    padding: 16px 24px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-bottom: 1px solid #e9e9e7;
    box-shadow: 0 1px 3px rgb(0 0 0 / 6%);
}

.header h1 {
    font-size: 1.25rem;
    font-weight: 600;
    color: #37352f;
    letter-spacing: -0.01em;
}

.status-indicators {
    display: flex;
    gap: 12px;
}

.status-item {
    display: flex;
    align-items: center;
    gap: 6px;
    padding: 6px 12px;
    background: #f7f6f3;
    border: 1px solid #e9e9e7;
    border-radius: 6px;
    font-size: 0.875rem;
    font-weight: 500;
    transition: all 0.15s ease;
}

.status-item:hover {
    background: #f1f1ef;
    border-color: #d3d1cb;
}

.status-icon {
    font-size: 1rem;
}

/* Main Content Layout */
.main-content {
    /* Clean up old flexbox layout - CSS Grid handles this now */
    display: contents; /* This makes the element itself disappear for layout purposes */
}

/* Responsive breakpoints */
@media (width <= 1200px) {
    .performance-toolbar {
        width: 240px;
        min-width: 240px;
    }

    .side-panel {
        min-width: 300px;
        max-width: 350px;
    }
}

@media (width <= 1000px) {
    .performance-toolbar {
        width: 200px;
        min-width: 200px;
    }

    .side-panel {
        min-width: 280px;
        max-width: 320px;
    }

    .chrome-viewport {
        min-width: 500px;
    }
}

@media (width <= 800px) {
    .performance-toolbar {
        width: 180px;
        min-width: 180px;
    }

    .side-panel {
        min-width: 250px;
        max-width: 280px;
    }

    .chrome-viewport {
        min-width: 400px;
    }
}

/* Performance Toolbar */
.performance-toolbar {
    grid-area: toolbar;
    width: 280px; /* Default width */
    min-width: 240px; /* Minimum width */
    background: #fff;
    border-right: 1px solid #e9e9e7;
    display: flex;
    flex-direction: column;
    transition: all 0.3s ease;
    box-shadow: 1px 0 3px rgb(0 0 0 / 4%);
    position: relative;
    overflow: auto;
    resize: horizontal; /* Allow user to resize */
}

.performance-toolbar:hover {
    box-shadow: 2px 0 8px rgb(0 0 0 / 8%);
}

.performance-toolbar.collapsed {
    width: 50px;
    min-width: 50px;
}

.toolbar-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 16px 20px;
    background: #fff;
    border-bottom: 1px solid #e9e9e7;
}

.toolbar-header h3 {
    font-size: 0.875rem;
    font-weight: 600;
    color: #37352f;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.toolbar-toggle {
    cursor: pointer;
    font-size: 1rem;
    color: #9b9a97;
    padding: 6px;
    border-radius: 4px;
    transition: all 0.15s ease;
    background: transparent;
    border: none;
}

.toolbar-toggle:hover {
    background: #f7f6f3;
    color: #37352f;
}

.performance-toolbar.collapsed .toolbar-toggle {
    transform: rotate(180deg);
}

/* Metrics Section */
.metrics-section {
    padding: 20px 16px;
    border-bottom: 1px solid #e9e9e7;
    overflow-y: auto;
    max-height: 300px;
    flex-shrink: 0;
    scrollbar-width: thin;
    scrollbar-color: #d3d1cb transparent;
}

.metrics-section::-webkit-scrollbar {
    width: 4px;
}

.metrics-section::-webkit-scrollbar-track {
    background: transparent;
}

.metrics-section::-webkit-scrollbar-thumb {
    background: #d3d1cb;
    border-radius: 2px;
    transition: background 0.15s ease;
}

.metrics-section::-webkit-scrollbar-thumb:hover {
    background: #9b9a97;
}

.metric-card {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 12px 14px;
    background: #fff;
    border: 1px solid #e9e9e7;
    border-radius: 8px;
    margin-bottom: 8px;
    transition: all 0.15s ease;
    box-shadow: 0 1px 3px rgb(0 0 0 / 5%);
}

.metric-card:hover {
    background: #fafafa;
    border-color: #d3d1cb;
    box-shadow: 0 2px 8px rgb(0 0 0 / 8%);
    transform: translateY(-1px);
}

.metric-icon {
    font-size: 1.25rem;
    width: 24px;
    text-align: center;
    opacity: 0.8;
}

.metric-info {
    flex: 1;
}

.metric-label {
    font-size: 0.75rem;
    font-weight: 500;
    color: #9b9a97;
    margin-bottom: 4px;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.metric-value {
    font-size: 1.125rem;
    font-weight: 600;
    color: #37352f;
    margin-bottom: 6px;
}

.metric-bar {
    width: 100%;
    height: 3px;
    background: #f7f6f3;
    border-radius: 2px;
    overflow: hidden;
}

.metric-progress {
    height: 100%;
    background: linear-gradient(90deg, #0f70b8, #4285f4);
    border-radius: 2px;
    transition: width 0.4s ease;
    width: 0%;
}

/* AI Tracking Section */
.ai-tracking-section {
    padding: 20px 16px;
    border-bottom: 1px solid #e9e9e7;
    background: #fff;
    overflow-y: auto;
    max-height: 250px;
    flex-shrink: 0;
    scrollbar-width: thin;
    scrollbar-color: #d3d1cb transparent;
}

.ai-tracking-section::-webkit-scrollbar {
    width: 4px;
}

.ai-tracking-section::-webkit-scrollbar-track {
    background: transparent;
}

.ai-tracking-section::-webkit-scrollbar-thumb {
    background: #d3d1cb;
    border-radius: 2px;
    transition: background 0.15s ease;
}

.ai-tracking-section::-webkit-scrollbar-thumb:hover {
    background: #9b9a97;
}

/* H4 selectors - moved up for CSS specificity ordering */
.message-queue h4 {
    margin: 0 0 16px;
    font-size: 1rem;
    font-weight: 600;
    color: #37352f;
}

.session-list h4 {
    margin: 0 0 12px;
    font-size: 0.875rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.ai-tracking-section .section-header h4 {
    font-size: 0.875rem;
    font-weight: 600;
    color: #37352f;
    margin-bottom: 16px;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.ai-status {
    margin-bottom: 16px;
    padding: 12px;
