    background: #9b9a97;
}

.metric-card {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 12px 14px;
    background: #fff;
    border: 1px solid #e9e9e7;
    border-radius: 8px;
    margin-bottom: 8px;
