}

.metric-icon {
    font-size: 1.25rem;
    width: 24px;
    text-align: center;
    opacity: 0.8;
}

.metric-info {
    flex: 1;
}

.metric-label {
    font-size: 0.75rem;
    font-weight: 500;
    color: #9b9a97;
    margin-bottom: 4px;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.metric-value {
    font-size: 1.125rem;
    font-weight: 600;
    color: #37352f;
    margin-bottom: 6px;
}

.metric-bar {
    width: 100%;
    height: 3px;
    background: #f7f6f3;
    border-radius: 2px;
    overflow: hidden;
}

.metric-progress {
    height: 100%;
    background: linear-gradient(90deg, #0f70b8, #4285f4);
    border-radius: 2px;
    transition: width 0.4s ease;
    width: 0%;
}

/* AI Tracking Section */
.ai-tracking-section {
    padding: 20px 16px;
    border-bottom: 1px solid #e9e9e7;
    background: #fff;
    overflow-y: auto;
    max-height: 250px;
    flex-shrink: 0;
    scrollbar-width: thin;
    scrollbar-color: #d3d1cb transparent;
}

.ai-tracking-section::-webkit-scrollbar {
    width: 4px;
}

.ai-tracking-section::-webkit-scrollbar-track {
    background: transparent;
}

.ai-tracking-section::-webkit-scrollbar-thumb {
    background: #d3d1cb;
    border-radius: 2px;
    transition: background 0.15s ease;
}

.ai-tracking-section::-webkit-scrollbar-thumb:hover {
    background: #9b9a97;
}

/* H4 selectors - moved up for CSS specificity ordering */
.message-queue h4 {
    margin: 0 0 16px;
    font-size: 1rem;
    font-weight: 600;
    color: #37352f;
}

.session-list h4 {
    margin: 0 0 12px;
    font-size: 0.875rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.ai-tracking-section .section-header h4 {
    font-size: 0.875rem;
    font-weight: 600;
    color: #37352f;
    margin-bottom: 16px;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.ai-status {
    margin-bottom: 16px;
    padding: 12px;
    background: #f7f6f3;
    border-radius: 8px;
    border: 1px solid #e9e9e7;
}

.ai-indicator {
    display: flex;
    align-items: center;
    gap: 10px;
    margin-bottom: 8px;
}

.status-dot {
    width: 8px;
    height: 8px;
    border-radius: 50%;
