}

.status-dot.error {
    background: #ef4444;
    box-shadow: 0 0 8px rgb(239 68 68 / 30%);
}

@keyframes pulse {
    0%, 100% { opacity: 1; }

    50% { opacity: 0.6; }
}

.last-action {
    font-size: 0.8125rem;
    color: #6b7280;
    font-style: italic;
    line-height: 1.4;
}

.action-log {
