}

.action-item {
    font-size: 0.8125rem;
    color: #37352f;
    padding: 4px 0;
    border-bottom: 1px solid #f3f4f6;
    line-height: 1.4;
}

.action-item:last-child {
    border-bottom: none;
}

.action-timestamp {
    color: #9b9a97;
    font-size: 0.75rem;
    margin-top: 2px;
}

/* Connection Status Section */
.connection-status-section {
    padding: 20px 16px;
    background: #fff;
    overflow-y: auto;
    max-height: 200px;
}

.connection-status-section .section-header h4 {
