/* Button Styles - Notion-inspired */
.btn {
    padding: 8px 14px;
    border: 1px solid transparent;
    border-radius: 6px;
    font-size: 0.875rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.15s ease;
    display: inline-flex;
    align-items: center;
    gap: 6px;
    font-family: inherit;
    white-space: nowrap;
    position: relative;
    overflow: hidden;
}

.btn:hover {
    transform: translateY(-0.5px);
    box-shadow: 0 4px 12px rgb(0 0 0 / 15%);
}

.btn:active {
    transform: translateY(0);
}

.btn-primary {
    background: #0f70b8;
    color: white;
    border-color: #0f70b8;
}

.btn-primary:hover {
    background: #0d5fa3;
    box-shadow: 0 4px 12px rgb(15 112 184 / 30%);
}

.btn-secondary {
    background: #fff;
    color: #37352f;
    border-color: #e9e9e7;
}

.btn-secondary:hover {
    background: #f7f6f3;
    border-color: #d3d1cb;
}

.btn-warning {
    background: #f59e0b;
    color: white;
    border-color: #f59e0b;
}

.btn-warning:hover {
    background: #d97706;
    box-shadow: 0 4px 12px rgb(245 158 11 / 30%);
}

.btn-danger {
    background: #ef4444;
    color: white;
    border-color: #ef4444;
}

.btn-danger:hover {
    background: #dc2626;
    box-shadow: 0 4px 12px rgb(239 68 68 / 30%);
}

.btn-info {
    background: #06b6d4;
    color: white;
    border-color: #06b6d4;
}

.btn-info:hover {
    background: #0891b2;
    box-shadow: 0 4px 12px rgb(6 182 212 / 30%);
}

.btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
}

.btn:disabled:hover {
    transform: none;
    box-shadow: none;
}

/* Modal Styles */
.modal {
    position: fixed;
    inset: 0;
    background: rgb(0 0 0 / 50%);
    backdrop-filter: blur(4px);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
    animation: fade-in 0.2s ease;
}

@keyframes fade-in {
    from { opacity: 0; }

    to { opacity: 1; }
}

.modal-content {
    background: #fff;
    border-radius: 12px;
    width: 85%;
    max-width: 700px;
    max-height: 85%;
    display: flex;
    flex-direction: column;
    box-shadow: 0 25px 50px -12px rgb(0 0 0 / 25%);
    border: 1px solid #e9e9e7;
    animation: slide-up 0.3s ease;
}

@keyframes slide-up {
    from { 
        opacity: 0;
        transform: translateY(20px) scale(0.95);
    }

    to { 
        opacity: 1;
        transform: translateY(0) scale(1);
