}

.screenshot-btn:hover {
    background: #e9ecef;
    border-color: #ced4da;
    color: #343a40;
}

.screenshot-btn:active {
    background: #dee2e6;
    transform: translateY(1px);
}

.screenshot-content img {
    max-width: 100%;
    height: auto;
    border-radius: 6px;
