    font-weight: bold;
}

.chrome-line.success {
    color: #4caf50;
}

/* Real Terminal Container */
.real-terminal-container {
    flex: 1;
    width: 100%;
    height: 100%;
    background: #1e1e1e;
    min-height: 300px;
}

/* xterm.js overrides moved up for CSS specificity ordering */

/* Responsive Design */
@media (width <= 1200px) {
    .container {
        grid-template: 
            "header header header" auto 
            "toolbar viewport sidebar" minmax(300px, 1fr) 
            "footer footer footer" auto / 
            minmax(180px, 20%) 1fr minmax(200px, 25%);
    }

    .performance-toolbar {
        width: 180px;
    }

    .side-panel {
        width: 200px;
    }
}

@media (width <= 900px) {
    .container {
        grid-template: 
            "header header" auto 
            "viewport sidebar" minmax(300px, 1fr) 
            "footer footer" auto / 
            1fr minmax(200px, 35%);
        grid-template-areas: 
            "header header"
            "viewport sidebar"
            "footer footer";
    }

    .performance-toolbar {
        display: none;
    }
}

@media (width <= 600px) {
    .container {
        grid-template: 
            "header" auto 
            "viewport" 1fr 
            "footer" auto / 
            1fr;
        grid-template-areas: 
            "header"
            "viewport"
            "footer";
    }

    .side-panel {
        display: none;
    }

    .header {
        flex-direction: column;
        gap: 8px;
        align-items: flex-start;
    }

    .status-indicators {
        flex-direction: column;
        gap: 4px;
        align-items: flex-start;
        width: 100%;
    }

    #header-screenshot-btn {
        align-self: flex-end;
        margin-left: 0 !important;
    }
}

/* Command History Indicator */
.command-history-indicator {
    position: absolute;
    right: 16px;
    top: 50%;
    transform: translateY(-50%);
    color: #6a9955;
    font-size: 11px;
    opacity: 0.7;
}

/* Screenshots Section Styles */
.screenshots-section {
    padding: 15px;
    border-bottom: 1px solid #e9e9e7;
    background: #fff;
}

.screenshots-section .section-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 15px;
}

.screenshot-controls {
    display: flex;
    align-items: center;
    gap: 10px;
}

.screenshot-count {
    font-size: 11px;
    color: #666;
    background: rgb(0 0 0 / 5%);
    padding: 2px 8px;
    border-radius: 12px;
    font-weight: 500;
}

.screenshots-container {
    max-height: 400px;
    overflow-y: auto;
    border: 1px solid #e9e9e7;
    border-radius: 6px;
    background: #fafafa;
}

.no-screenshots {
    text-align: center;
    padding: 40px 20px;
    color: #999;
}

.no-screenshots-icon {
    font-size: 32px;
    display: block;
    margin-bottom: 10px;
    opacity: 0.6;
}

.no-screenshots p {
    margin: 0 0 5px;
    font-size: 14px;
    color: #666;
}

.no-screenshots small {
    font-size: 12px;
    opacity: 0.7;
}

.screenshot-item {
    border-bottom: 1px solid #e9e9e7;
    background: #fff;
    transition: all 0.2s ease;
}

.screenshot-item:hover {
    background: #f8f8f7;
}

.screenshot-item:last-child {
    border-bottom: none;
}

.screenshot-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 12px 15px;
    cursor: pointer;
    user-select: none;
    transition: background-color 0.2s;
}

.screenshot-header:hover {
    background: rgb(0 0 0 / 2%);
}

.screenshot-info {
    display: flex;
    align-items: center;
    gap: 12px;
    font-size: 13px;
}

.screenshot-timestamp {
    color: #666;
    font-family: 'SF Mono', Monaco, monospace;
    font-size: 12px;
    font-weight: 500;
}

.screenshot-size {
    color: #888;
    font-size: 11px;
    background: rgb(0 0 0 / 5%);
    padding: 2px 6px;
    border-radius: 8px;
}

.screenshot-toggle {
    font-size: 12px;
    color: #06c;
    font-weight: 600;
    transition: transform 0.2s ease;
}

.screenshot-toggle.expanded {
    transform: rotate(90deg);
}

.screenshot-content {
    display: none;
    padding: 0 15px 15px;
    animation: slide-down 0.2s ease;
}

.screenshot-content.expanded {
    display: block;
}

@keyframes slide-down {
    from {
        opacity: 0;
        transform: translateY(-10px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.screenshot-image {
    max-width: 100%;
    border-radius: 6px;
    border: 1px solid #e9e9e7;
    box-shadow: 0 2px 12px rgb(0 0 0 / 8%);
    transition: transform 0.2s ease;
}

.screenshot-image:hover {
    transform: scale(1.02);
    box-shadow: 0 4px 20px rgb(0 0 0 / 12%);
}

.screenshot-actions {
    margin-top: 12px;
    display: flex;
    gap: 8px;
}

.screenshot-path {
    margin-bottom: 10px;
    padding: 8px;
    background: rgb(0 0 0 / 3%);
    border-radius: 4px;
    border-left: 3px solid #06c;
}

.screenshot-path code {
    background: rgb(0 0 0 / 10%);
    padding: 2px 6px;
    border-radius: 3px;
    font-family: 'SF Mono', Monaco, monospace;
    font-size: 11px;
    color: #06c;
    cursor: pointer;
    transition: all 0.2s ease;
    user-select: all;
}

.screenshot-path code:hover {
    background: rgb(0 102 204 / 10%);
    color: #049;
}

.screenshot-btn {
    background: #f8f9fa;
    border: 1px solid #dee2e6;
    color: #495057;
    padding: 4px 8px;
    border-radius: 4px;
    font-size: 11px;
    cursor: pointer;
    transition: all 0.2s ease;
    white-space: nowrap;
}

.screenshot-btn:hover {
    background: #e9ecef;
    border-color: #ced4da;
    color: #343a40;
}

.screenshot-btn:active {
    background: #dee2e6;
    transform: translateY(1px);
}

.screenshot-content img {
    max-width: 100%;
    height: auto;
    border-radius: 6px;
    border: 1px solid #e9e9e7;
    box-shadow: 0 2px 12px rgb(0 0 0 / 8%);
    transition: transform 0.2s ease;
    display: block;
}

.screenshot-content img:hover {
    transform: scale(1.02);
    box-shadow: 0 4px 20px rgb(0 102 204 / 15%);
}

.btn-sm {
    padding: 6px 12px;
    font-size: 11px;
    border-radius: 4px;
    font-weight: 500;
}

/* .file-import merged with earlier definition */

