    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.ai-tracking-section .section-header h4 {
    font-size: 0.875rem;
    font-weight: 600;
    color: #37352f;
    margin-bottom: 16px;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.ai-status {
    margin-bottom: 16px;
    padding: 12px;
    background: #f7f6f3;
    border-radius: 8px;
