/* Reset text direction for the content inside the right sidebar */
.side-panel > * {
    direction: ltr;
}

/* Tab Navigation */
.tab-navigation {
    display: flex;
    background: #fff;
    border-bottom: 1px solid #e9e9e7;
    padding: 0;
}

.tab-btn {
    flex: 1;
    padding: 12px 8px;
    border: none;
    background: transparent;
    color: #9b9a97;
    font-size: 0.8125rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.15s ease;
    border-bottom: 2px solid transparent;
    position: relative;
}

.tab-btn:hover {
    background: #f7f6f3;
    color: #37352f;
}

.tab-btn.active {
    color: #0f70b8;
    border-bottom-color: #0f70b8;
    background: #fafafa;
}

.tab-btn.active::after {
    content: '';
    position: absolute;
    bottom: -1px;
    left: 0;
    right: 0;
    height: 1px;
    background: #fafafa;
}

/* Tab Content */
.tab-content {
    flex: 1;
    display: none;
    flex-direction: column;
    overflow: hidden;
    position: relative;
}

.tab-content.active {
    display: flex;
}

/* Enhanced scrolling for tab content */
.tab-content > * {
    overflow-y: auto;
    scrollbar-width: thin;
    scrollbar-color: #d3d1cb transparent;
}

.tab-content > *::-webkit-scrollbar {
