/* Button Styles - Notion-inspired */
.btn {
    padding: 8px 14px;
    border: 1px solid transparent;
    border-radius: 6px;
    font-size: 0.875rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.15s ease;
    display: inline-flex;
    align-items: center;
    gap: 6px;
    font-family: inherit;
    white-space: nowrap;
    position: relative;
    overflow: hidden;
}

.btn:hover {
    transform: translateY(-0.5px);
    box-shadow: 0 4px 12px rgb(0 0 0 / 15%);
}

.btn:active {
    transform: translateY(0);
}

.btn-primary {
    background: #0f70b8;
    color: white;
    border-color: #0f70b8;
}

.btn-primary:hover {
    background: #0d5fa3;
