/* Modal Styles */
.modal {
    position: fixed;
    inset: 0;
    background: rgb(0 0 0 / 50%);
    backdrop-filter: blur(4px);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
    animation: fade-in 0.2s ease;
}

@keyframes fade-in {
    from { opacity: 0; }

    to { opacity: 1; }
}

.modal-content {
    background: #fff;
    border-radius: 12px;
    width: 85%;
    max-width: 700px;
    max-height: 85%;
    display: flex;
    flex-direction: column;
    box-shadow: 0 25px 50px -12px rgb(0 0 0 / 25%);
    border: 1px solid #e9e9e7;
    animation: slide-up 0.3s ease;
}

@keyframes slide-up {
    from { 
        opacity: 0;
        transform: translateY(20px) scale(0.95);
    }

    to { 
        opacity: 1;
        transform: translateY(0) scale(1);
    }
}

.modal-header {
    display: flex;
