    align-items: flex-start;
}

.terminal-line.command {
    color: #569cd6;
    position: relative;
}

.terminal-line.command::before {
    content: '$ ';
    color: #4ec9b0;
    font-weight: bold;
    margin-right: 4px;
}

.terminal-line.output {
    color: #d4d4d4;
    padding-left: 12px;
}

.terminal-line.error {
    color: #f48771;
    padding-left: 12px;
}

.terminal-line.warning {
    color: #ff9800;
    padding-left: 12px;
}

.terminal-line.success {
    color: #4caf50;
    padding-left: 12px;
}

.terminal-input-line {
    display: flex;
    align-items: center;
    background: #1e1e1e;
    border-top: 1px solid #3e3e42;
    padding: 12px 16px;
    min-height: 40px;
}

.terminal-prompt {
    color: #4ec9b0;
    font-weight: bold;
    margin-right: 8px;
    font-size: 13px;
    user-select: none;
}

.terminal-input {
    flex: 1;
    background: transparent;
    border: none;
    color: #d4d4d4;
    font-family: inherit;
    font-size: 13px;
    outline: none;
    line-height: 1.4;
}

.terminal-input::placeholder {
    color: #6a9955;
    font-style: italic;
}

.terminal-input:focus {
    background: rgb(255 255 255 / 2%);
    border-radius: 2px;
}

/* VS Code Terminal Suggestions */
.terminal-suggestions {
    position: absolute;
    bottom: 100%;
    left: 16px;
    right: 16px;
    background: #252526;
    border: 1px solid #3e3e42;
    border-radius: 4px;
    max-height: 200px;
    overflow-y: auto;
    z-index: 1000;
    display: none;
}

.suggestion-item {
    padding: 8px 12px;
    color: #ccc;
    cursor: pointer;
    font-size: 13px;
    border-bottom: 1px solid #3e3e42;
}

.suggestion-item:hover,
.suggestion-item.selected {
    background: #094771;
    color: #fff;
}

.suggestion-item:last-child {
    border-bottom: none;
}

.suggestion-description {
    color: #9cdcfe;
    font-size: 11px;
    margin-top: 2px;
}

/* VS Code Style Terminal Tabs */
.terminal-tabs {
    display: flex;
    align-items: center;
    background: #2d2d30;
    border-bottom: 1px solid #3e3e42;
    padding: 0 8px;
    min-height: 35px;
    position: relative;
    overflow-x: auto;
    scrollbar-width: none;
}

.terminal-tabs::-webkit-scrollbar {
    display: none;
}

.terminal-tab-controls {
    margin-left: auto;
    display: flex;
    align-items: center;
    gap: 4px;
}

.terminal-control-btn {
    width: 24px;
    height: 24px;
    border: none;
    background: none;
    color: #ccc;
    cursor: pointer;
    border-radius: 3px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 11px;
    transition: all 0.15s ease;
}

.terminal-control-btn:hover {
    background: #37373d;
    color: #fff;
}

/* Terminal Content Areas */
.terminal-content {
    display: none;
    flex-direction: column;
    height: 100%;
    background: #1e1e1e;
}

.terminal-content.active {
    display: flex;
}

/* Enhanced Terminal Controls */
.terminal-status-bar {
    display: flex;
    align-items: center;
    gap: 12px;
    flex: 1;
}

.terminal-info {
    display: flex;
    align-items: center;
    gap: 8px;
    margin-left: auto;
    font-size: 11px;
    color: #999;
}

.terminal-shell {
    background: #007acc;
    color: #fff;
    padding: 2px 6px;
    border-radius: 3px;
    font-weight: 500;
}

.terminal-path {
    font-family: Consolas, 'Courier New', 'SF Mono', Monaco, monospace;
}

/* Debug Console Specific Styles */
.debug-output {
    background: #0e1419;
    border: 1px solid #1e2328;
}

.debug-prompt {
    color: #ff6b35 !important;
    font-weight: bold;
}

.debug-status {
    padding: 2px 6px;
    border-radius: 3px;
    font-size: 11px;
    font-weight: 500;
}

.debug-status.connected {
    background: #4caf50;
    color: #fff;
}

.debug-status.disconnected {
    background: #f44336;
    color: #fff;
}

/* Logs Terminal Specific Styles */
.logs-output {
    font-family: 'SF Mono', Consola, Menlo, Monaco, monospace;
    font-size: 12px;
    line-height: 1.5;
}

.log-level-select {
    background: #3c3c3c;
    border: 1px solid #555;
    color: #ccc;
    padding: 4px 8px;
    border-radius: 3px;
    font-size: 11px;
}

.log-filter {
    background: #007acc;
    color: #fff;
    padding: 2px 6px;
    border-radius: 3px;
    font-size: 11px;
    font-weight: 500;
}

/* Chrome Console Specific Styles */
.chrome-console-output {
    background: #242424;
    border: 1px solid #333;
}

.chrome-prompt {
    color: #61dafb !important;
    font-weight: bold;
}

.chrome-status {
    padding: 2px 6px;
    border-radius: 3px;
    font-size: 11px;
    font-weight: 500;
}

.chrome-status.connected {
    background: #4caf50;
    color: #fff;
}

.chrome-status.disconnected {
    background: #f44336;
    color: #fff;
}

/* Log Entry Styling */
.log-entry {
    display: flex;
    align-items: flex-start;
    gap: 8px;
    padding: 3px 0;
    border-bottom: 1px solid #2a2a2a;
}

.log-timestamp {
    color: #888;
    font-size: 11px;
    min-width: 80px;
    font-family: monospace;
}

.log-level {
    padding: 1px 4px;
    border-radius: 2px;
    font-size: 10px;
    font-weight: 500;
    min-width: 50px;
    text-align: center;
}

.log-level.error {
    background: #f44336;
    color: #fff;
}

.log-level.warn {
    background: #ff9800;
    color: #fff;
}

.log-level.info {
    background: #2196f3;
    color: #fff;
}

.log-level.debug {
    background: #9c27b0;
    color: #fff;
}

.log-message {
    flex: 1;
    color: #d4d4d4;
    word-break: break-all;
}

/* Terminal Maximized State */
.terminal-section.maximized {
    position: fixed !important;
    inset: 0;
    z-index: 9999;
    margin: 0;
    border-radius: 0;
    width: 100vw !important;
    height: 100vh !important;
}

/* Enhanced Terminal Line Types (merged with earlier definitions) */

.terminal-line.info {
    color: #2196f3;
}

.debug-line.command {
    color: #ff6b35;
    font-weight: bold;
}

.debug-line.success {
    color: #4caf50;
}

.chrome-line.command {
    color: #61dafb;
    font-weight: bold;
}

.chrome-line.success {
    color: #4caf50;
}

/* Real Terminal Container */
.real-terminal-container {
    flex: 1;
    width: 100%;
    height: 100%;
    background: #1e1e1e;
    min-height: 300px;
}

