/* Responsive Design */
@media (width <= 1200px) {
    .container {
        grid-template: 
            "header header header" auto 
            "toolbar viewport sidebar" minmax(300px, 1fr) 
            "footer footer footer" auto / 
            minmax(180px, 20%) 1fr minmax(200px, 25%);
    }

    .performance-toolbar {
        width: 180px;
    }

    .side-panel {
        width: 200px;
    }
}

@media (width <= 900px) {
    .container {
        grid-template: 
            "header header" auto 
            "viewport sidebar" minmax(300px, 1fr) 
            "footer footer" auto / 
            1fr minmax(200px, 35%);
        grid-template-areas: 
            "header header"
            "viewport sidebar"
            "footer footer";
    }

    .performance-toolbar {
        display: none;
    }
}

@media (width <= 600px) {
    .container {
        grid-template: 
            "header" auto 
            "viewport" 1fr 
            "footer" auto / 
            1fr;
        grid-template-areas: 
            "header"
            "viewport"
            "footer";
    }

    .side-panel {
        display: none;
    }

    .header {
        flex-direction: column;
        gap: 8px;
        align-items: flex-start;
