/* Tab Navigation */
.tab-navigation {
    display: flex;
    background: #fff;
    border-bottom: 1px solid #e9e9e7;
    padding: 0;
}

.tab-btn {
    flex: 1;
    padding: 12px 8px;
    border: none;
    background: transparent;
    color: #9b9a97;
    font-size: 0.8125rem;
