    gap: 12px;
    font-size: 13px;
}

.screenshot-timestamp {
    color: #666;
    font-family: 'SF Mono', Monaco, monospace;
    font-size: 12px;
    font-weight: 500;
}

.screenshot-size {
    color: #888;
    font-size: 11px;
    background: rgb(0 0 0 / 5%);
    padding: 2px 6px;
    border-radius: 8px;
}

.screenshot-toggle {
    font-size: 12px;
    color: #06c;
    font-weight: 600;
    transition: transform 0.2s ease;
}

.screenshot-toggle.expanded {
    transform: rotate(90deg);
}

.screenshot-content {
    display: none;
    padding: 0 15px 15px;
    animation: slide-down 0.2s ease;
}

.screenshot-content.expanded {
    display: block;
}

@keyframes slide-down {
    from {
        opacity: 0;
        transform: translateY(-10px);
    }

    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.screenshot-image {
    max-width: 100%;
    border-radius: 6px;
    border: 1px solid #e9e9e7;
    box-shadow: 0 2px 12px rgb(0 0 0 / 8%);
    transition: transform 0.2s ease;
}

.screenshot-image:hover {
    transform: scale(1.02);
    box-shadow: 0 4px 20px rgb(0 0 0 / 12%);
}

.screenshot-actions {
    margin-top: 12px;
    display: flex;
    gap: 8px;
}

.screenshot-path {
    margin-bottom: 10px;
    padding: 8px;
    background: rgb(0 0 0 / 3%);
    border-radius: 4px;
    border-left: 3px solid #06c;
}

.screenshot-path code {
    background: rgb(0 0 0 / 10%);
    padding: 2px 6px;
    border-radius: 3px;
    font-family: 'SF Mono', Monaco, monospace;
    font-size: 11px;
    color: #06c;
    cursor: pointer;
    transition: all 0.2s ease;
    user-select: all;
}

.screenshot-path code:hover {
    background: rgb(0 102 204 / 10%);
    color: #049;
}

.screenshot-btn {
    background: #f8f9fa;
    border: 1px solid #dee2e6;
    color: #495057;
    padding: 4px 8px;
    border-radius: 4px;
    font-size: 11px;
    cursor: pointer;
    transition: all 0.2s ease;
    white-space: nowrap;
}

.screenshot-btn:hover {
    background: #e9ecef;
    border-color: #ced4da;
    color: #343a40;
}

.screenshot-btn:active {
    background: #dee2e6;
    transform: translateY(1px);
}

.screenshot-content img {
    max-width: 100%;
    height: auto;
    border-radius: 6px;
    border: 1px solid #e9e9e7;
    box-shadow: 0 2px 12px rgb(0 0 0 / 8%);
    transition: transform 0.2s ease;
    display: block;
}

.screenshot-content img:hover {
    transform: scale(1.02);
    box-shadow: 0 4px 20px rgb(0 102 204 / 15%);
}

.btn-sm {
    padding: 6px 12px;
    font-size: 11px;
    border-radius: 4px;
    font-weight: 500;
}

