        tabButtons.forEach(btn => btn.classList.remove('active'));
        tabContents.forEach(content => content.classList.remove('active'));

        // Activate planning tab
        planningTabBtn.classList.add('active');
        planningTabContent.classList.add('active');

        console.log('âœ… Switched to planning tab');

        // If screenshot data was sent, add it to the collection
        if (event.data.screenshot) {
