.section-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 16px 20px;
    background: #fff;
    border-bottom: 1px solid #e9e9e7;
}

/* VS Code Terminal Header Override */
.vscode-terminal-header {
    background: #2d2d30 !important;
    border-bottom: 1px solid #3c3c3c !important;
    padding: 4px 8px !important;
    display: flex !important;
    align-items: center !important;
    justify-content: space-between !important;
    min-height: 32px !important;
}

.terminal-tab-bar {
    display: flex;
    align-items: center;
    gap: 4px;
}

.terminal-tab {
    display: flex;
    align-items: center;
    gap: 6px;
    padding: 4px 12px;
    background: #1e1e1e;
    color: #ccc;
    border-radius: 4px 4px 0 0;
    font-size: 12px;
    border: 1px solid #3c3c3c;
    border-bottom: none;
}

.terminal-tab.active {
    background: #181818;
    color: #fff;
}

.terminal-tab .tab-icon {
    font-size: 10px;
    opacity: 0.8;
}

.terminal-tab .tab-title {
    font-weight: 400;
    font-family: 'Cascadia Code', 'SF Mono', Monaco, monospace;
    flex: 1;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

.tab-close-btn {
    background: none;
    border: none;
    color: #888;
    cursor: pointer;
    padding: 2px 4px;
    border-radius: 3px;
    margin-left: 4px;
    font-size: 14px;
    opacity: 0;
    transition: opacity 0.2s ease;
}

.tab-close-btn:hover {
    background: #555;
    color: #fff;
}

.terminal-tab:hover .tab-close-btn {
    opacity: 1;
}

.terminal-container {
    flex: 1;
    position: relative;
    background: #181818;
    overflow: hidden;
}

.terminal-instance {
    position: absolute;
    inset: 0;
    display: none;
}

.terminal-instance.active {
    display: block;
}

.terminal-wrapper {
    width: 100%;
    height: 100%;
    position: relative;
}

.xterm-container {
    width: 100%;
    height: 100%;
    padding: 8px;
}

.terminal-dropdown-menu {
    position: absolute;
    top: 32px;
    right: 8px;
    background: #2d2d30;
    border: 1px solid #3c3c3c;
    border-radius: 4px;
    box-shadow: 0 4px 12px rgb(0 0 0 / 30%);
    z-index: 1000;
    min-width: 200px;
}

.dropdown-item {
    display: flex;
    align-items: center;
    padding: 8px 12px;
    color: #ccc;
    cursor: pointer;
    transition: background 0.2s ease;
}

.dropdown-item:hover {
    background: #37373d;
}

.dropdown-icon {
    width: 16px;
    margin-right: 8px;
    font-size: 12px;
}

.dropdown-text {
    flex: 1;
    font-size: 13px;
}

.dropdown-shortcut {
    font-size: 11px;
    color: #888;
    margin-left: 8px;
}

.dropdown-divider {
    height: 1px;
    background: #3c3c3c;
    margin: 4px 0;
}

/* .section-header h2, h3 consolidated above for CSS specificity ordering */

.chrome-controls {
    display: flex;
    gap: 8px;
}

/* .chrome-container and its states consolidated above for CSS specificity ordering */

.chrome-placeholder {
    position: absolute;
    inset: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    background: linear-gradient(135deg, #f7f6f3 0%, #fff 100%);
}

.placeholder-content {
    text-align: center;
    color: #9b9a97;
}

.loading-spinner {
    width: 32px;
    height: 32px;
    border: 3px solid #e9e9e7;
    border-top: 3px solid #0f70b8;
    border-radius: 50%;
    animation: spin 1s linear infinite;
    margin: 0 auto 16px;
}

@keyframes spin {
    0% { transform: rotate(0deg); }

    100% { transform: rotate(360deg); }
}

.chrome-webview {
    width: 100%;
    height: 100%;
    border-radius: 8px;
    flex: 1;
    border: none;
    background: #fff;
}

.chrome-info {
    margin-top: 12px;
    font-size: 0.875rem;
    color: #9b9a97;
    font-weight: 500;
}

/* Side Panel consolidated above for CSS specificity ordering */

.side-panel:hover {
    box-shadow: -2px 0 8px rgb(0 0 0 / 8%);
}

/* Reset text direction for the content inside the right sidebar */
.side-panel > * {
    direction: ltr;
}

/* Tab Navigation */
.tab-navigation {
    display: flex;
    background: #fff;
    border-bottom: 1px solid #e9e9e7;
    padding: 0;
}

.tab-btn {
    flex: 1;
    padding: 12px 8px;
    border: none;
    background: transparent;
    color: #9b9a97;
    font-size: 0.8125rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.15s ease;
    border-bottom: 2px solid transparent;
    position: relative;
}

.tab-btn:hover {
    background: #f7f6f3;
    color: #37352f;
}

.tab-btn.active {
    color: #0f70b8;
    border-bottom-color: #0f70b8;
    background: #fafafa;
}

.tab-btn.active::after {
    content: '';
    position: absolute;
    bottom: -1px;
    left: 0;
    right: 0;
    height: 1px;
    background: #fafafa;
}

/* Tab Content */
.tab-content {
    flex: 1;
    display: none;
    flex-direction: column;
    overflow: hidden;
    position: relative;
}

.tab-content.active {
    display: flex;
}

/* Enhanced scrolling for tab content */
.tab-content > * {
    overflow-y: auto;
    scrollbar-width: thin;
    scrollbar-color: #d3d1cb transparent;
}

.tab-content > *::-webkit-scrollbar {
    width: 6px;
}

.tab-content > *::-webkit-scrollbar-track {
    background: transparent;
}

.tab-content > *::-webkit-scrollbar-thumb {
    background: #d3d1cb;
    border-radius: 3px;
    transition: background 0.15s ease;
}

.tab-content > *::-webkit-scrollbar-thumb:hover {
    background: #9b9a97;
}

/* Planning Guidance Section */
.planning-section {
    flex: 1;
    display: flex;
    flex-direction: column;
    border-bottom: 1px solid #e9e9e7;
    background: #fff;
}

.file-import {
    padding: 20px;
    border-bottom: 1px solid #e9e9e7;
    display: flex;
    gap: 10px;
    flex-wrap: wrap;
}

.imported-files {
    margin-top: 16px;
}

.file-item {
    display: flex;
    align-items: center;
    gap: 10px;
    padding: 10px 12px;
    background: #f7f6f3;
    border: 1px solid #e9e9e7;
    border-radius: 6px;
    margin-bottom: 8px;
    cursor: pointer;
    transition: all 0.15s ease;
}

.file-item:hover {
    background: #f1f1ef;
    border-color: #d3d1cb;
}

.file-item.template {
    border-color: #0f70b8;
    background: rgb(15 112 184 / 5%);
}

.file-icon {
    font-size: 1rem;
    opacity: 0.7;
}

.file-name {
    flex: 1;
    font-size: 0.875rem;
    font-weight: 500;
    color: #37352f;
}

.file-status {
    font-size: 0.875rem;
}

.guidance-preview {
    flex: 1;
    padding: 20px;
}

.guidance-preview textarea {
    width: 100%;
    height: 100%;
    background: #fff;
    border: 1px solid #e9e9e7;
    border-radius: 8px;
    padding: 16px;
    color: #37352f;
    font-family: 'SF Mono', Monaco, 'Cascadia Code', monospace;
    font-size: 0.8125rem;
    line-height: 1.6;
    resize: none;
    transition: border-color 0.15s ease;
}

/* General focus styles - moved up for CSS specificity ordering */
input:focus,
textarea:focus,
button:focus {
    outline: none;
}

.message-compose textarea {
    width: 100%;
    background: #fff;
    border: 1px solid #e9e9e7;
    border-radius: 8px;
    padding: 14px 16px;
    color: #37352f;
    font-family: inherit;
    font-size: 0.875rem;
    line-height: 1.5;
    resize: vertical;
    margin-bottom: 12px;
    transition: border-color 0.15s ease;
}

.guidance-preview textarea:focus {
    outline: none;
    border-color: #0f70b8;
    box-shadow: 0 0 0 3px rgb(15 112 184 / 10%);
}

/* User Input Section */
.user-input-section {
    flex: 1;
    display: flex;
    flex-direction: column;
    background: #fff;
}

.message-compose {
    padding: 20px;
    border-bottom: 1px solid #e9e9e7;
}

/* .message-compose textarea consolidated above for CSS specificity ordering */

.message-compose textarea:focus {
    outline: none;
    border-color: #0f70b8;
    box-shadow: 0 0 0 3px rgb(15 112 184 / 10%);
}

.message-compose textarea::placeholder {
    color: #9b9a97;
}

.message-controls {
    display: flex;
    gap: 8px;
}

.message-queue {
    flex: 1;
    padding: 20px;
    overflow-y: auto;
    scrollbar-width: thin;
    scrollbar-color: #d3d1cb transparent;
    max-height: calc(100vh - 300px);
}

.message-queue::-webkit-scrollbar {
    width: 6px;
}

.message-queue::-webkit-scrollbar-track {
    background: transparent;
}

.message-queue::-webkit-scrollbar-thumb {
    background: #d3d1cb;
    border-radius: 3px;
    transition: background 0.15s ease;
}

.message-queue::-webkit-scrollbar-thumb:hover {
    background: #9b9a97;
}

/* .message-queue h4 consolidated above for CSS specificity ordering */

.queued-messages {
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.message-item {
    background: #f7f6f3;
    border: 1px solid #e9e9e7;
    border-left: 3px solid #0f70b8;
    padding: 12px 16px;
    border-radius: 6px;
    font-size: 0.8125rem;
    line-height: 1.5;
    transition: all 0.15s ease;
}

.message-item:hover {
    background: #f1f1ef;
    border-left-color: #0d5fa3;
}

.message-item.urgent {
    border: 1px solid #fecaca;
    border-left-color: #ef4444;
    background: #fef2f2;
}

.message-item.urgent:hover {
    background: #fde8e8;
}

.message-meta {
    font-size: 0.75rem;
    color: #9b9a97;
    margin-top: 6px;
    font-weight: 500;
}

/* Bottom Panel */
.bottom-panel {
    grid-area: footer;
    background: #fff;
    border-top: 1px solid #e9e9e7;
    max-height: 200px;
    min-height: 150px;
    box-shadow: 0 -1px 3px rgb(0 0 0 / 6%);
    position: relative;
    overflow: auto; /* Allow resizing */
    transition: all 0.3s ease;
    resize: vertical; /* Ensure resize works correctly in a grid */
}

.bottom-panel:hover {
    box-shadow: 0 -2px 8px rgb(0 0 0 / 12%);
}

.connection-manager {
    padding: 20px;
}

.connection-info {
    display: flex;
    gap: 24px;
    margin-bottom: 20px;
    padding: 16px;
    background: #f7f6f3;
    border: 1px solid #e9e9e7;
    border-radius: 8px;
}

.info-group {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.info-label {
    font-size: 0.75rem;
    font-weight: 500;
    color: #9b9a97;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.info-value {
    font-size: 1rem;
    font-weight: 600;
    color: #0f70b8;
}

/* .session-list h4 consolidated above for CSS specificity ordering */

/* .session-container and :hover consolidated above for CSS specificity ordering */

.session-container::-webkit-scrollbar {
    width: 4px;
}

.session-container::-webkit-scrollbar-track {
    background: transparent;
}

.session-container::-webkit-scrollbar-thumb {
    background: #d3d1cb;
    border-radius: 2px;
    transition: background 0.15s ease;
}

.session-container::-webkit-scrollbar-thumb:hover {
    background: #9b9a97;
}

.session-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 8px 0;
    border-bottom: 1px solid #f3f4f6;
    font-size: 0.8125rem;
    color: #37352f;
}

.session-item:last-child {
    border-bottom: none;
}

.no-sessions {
    text-align: center;
    color: #9b9a97;
    font-style: italic;
    font-size: 0.8125rem;
}

.connection-controls {
    display: flex;
    gap: 10px;
}

/* Button Styles - Notion-inspired */
.btn {
    padding: 8px 14px;
    border: 1px solid transparent;
    border-radius: 6px;
    font-size: 0.875rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.15s ease;
    display: inline-flex;
    align-items: center;
    gap: 6px;
    font-family: inherit;
    white-space: nowrap;
    position: relative;
    overflow: hidden;
}

.btn:hover {
    transform: translateY(-0.5px);
    box-shadow: 0 4px 12px rgb(0 0 0 / 15%);
}

.btn:active {
    transform: translateY(0);
}

.btn-primary {
    background: #0f70b8;
    color: white;
    border-color: #0f70b8;
}

.btn-primary:hover {
    background: #0d5fa3;
    box-shadow: 0 4px 12px rgb(15 112 184 / 30%);
}

.btn-secondary {
    background: #fff;
    color: #37352f;
    border-color: #e9e9e7;
}

.btn-secondary:hover {
    background: #f7f6f3;
    border-color: #d3d1cb;
}

.btn-warning {
    background: #f59e0b;
    color: white;
    border-color: #f59e0b;
}

.btn-warning:hover {
    background: #d97706;
    box-shadow: 0 4px 12px rgb(245 158 11 / 30%);
}

.btn-danger {
    background: #ef4444;
    color: white;
    border-color: #ef4444;
}

.btn-danger:hover {
    background: #dc2626;
    box-shadow: 0 4px 12px rgb(239 68 68 / 30%);
}

.btn-info {
    background: #06b6d4;
    color: white;
    border-color: #06b6d4;
}

.btn-info:hover {
    background: #0891b2;
    box-shadow: 0 4px 12px rgb(6 182 212 / 30%);
}

.btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
}

.btn:disabled:hover {
    transform: none;
    box-shadow: none;
}

/* Modal Styles */
.modal {
    position: fixed;
    inset: 0;
    background: rgb(0 0 0 / 50%);
    backdrop-filter: blur(4px);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
    animation: fade-in 0.2s ease;
}

@keyframes fade-in {
    from { opacity: 0; }

    to { opacity: 1; }
}

.modal-content {
    background: #fff;
    border-radius: 12px;
    width: 85%;
    max-width: 700px;
    max-height: 85%;
    display: flex;
    flex-direction: column;
    box-shadow: 0 25px 50px -12px rgb(0 0 0 / 25%);
    border: 1px solid #e9e9e7;
    animation: slide-up 0.3s ease;
}

@keyframes slide-up {
    from { 
        opacity: 0;
        transform: translateY(20px) scale(0.95);
    }

    to { 
        opacity: 1;
        transform: translateY(0) scale(1);
    }
}

.modal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px 24px;
    border-bottom: 1px solid #e9e9e7;
}

/* .modal-header h3 consolidated above for CSS specificity ordering */

.modal-close {
    background: #f7f6f3;
    border: 1px solid #e9e9e7;
    color: #9b9a97;
    font-size: 1.25rem;
    cursor: pointer;
    padding: 6px 8px;
    border-radius: 6px;
    transition: all 0.15s ease;
    width: 32px;
    height: 32px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.modal-close:hover {
    background: #f1f1ef;
    border-color: #d3d1cb;
    color: #37352f;
}

.modal-body {
    flex: 1;
    padding: 24px;
    overflow-y: auto;
}

.modal-body pre {
    background: #f7f6f3;
    border: 1px solid #e9e9e7;
    border-radius: 8px;
    padding: 20px;
    color: #37352f;
    font-size: 0.8125rem;
    line-height: 1.6;
    white-space: pre-wrap;
    font-family: 'SF Mono', Monaco, 'Cascadia Code', monospace;
}

/* Utility Classes */
.hidden {
    display: none !important;
}

.visible {
    display: block !important;
}

/* Status Colors */
.status-running {
    color: #27ae60;
}

.status-error {
    color: #e74c3c;
}

.status-warning {
    color: #f39c12;
}

/* Scrollbar Styling - Notion-inspired */
::-webkit-scrollbar {
    width: 6px;
}

::-webkit-scrollbar-track {
    background: transparent;
}

::-webkit-scrollbar-thumb {
    background: #d3d1cb;
    border-radius: 3px;
    transition: background 0.15s ease;
}

::-webkit-scrollbar-thumb:hover {
    background: #9b9a97;
}

/* Additional Notion-style enhancements */
.section-divider {
    height: 1px;
    background: linear-gradient(90deg, transparent, #e9e9e7, transparent);
    margin: 20px 0;
}

/* Responsive Layout Classes */
.compact-layout .performance-toolbar {
    width: 240px;
    min-width: 240px;
}

.compact-layout .side-panel {
    min-width: 300px;
    max-width: 350px;
}

.mobile-layout .performance-toolbar {
    width: 200px;
    min-width: 200px;
}

.mobile-layout .side-panel {
    min-width: 280px;
    max-width: 320px;
}

.mobile-layout .chrome-viewport {
    min-width: 500px;
}

.short-layout .bottom-panel {
    max-height: 150px;
    min-height: 120px;
}

/* .short-layout selectors consolidated above for CSS specificity ordering */

/* Base selectors consolidated - removed to avoid duplicates */

/* Terminal scrollbar styles (moved up for specificity ordering) */
.terminal-output::-webkit-scrollbar-thumb {
    background: #424242;
    border-radius: 4px;
}

/* Enhanced scrollbar visibility on hover */
.metrics-section:hover::-webkit-scrollbar-thumb,
.ai-tracking-section:hover::-webkit-scrollbar-thumb,
.connection-status-section:hover::-webkit-scrollbar-thumb,
.message-queue:hover::-webkit-scrollbar-thumb,
.session-container:hover::-webkit-scrollbar-thumb,
.error-log:hover::-webkit-scrollbar-thumb,
.terminal-output:hover::-webkit-scrollbar-thumb {
    background: #9b9a97;
}

/* Transitions moved to individual selectors to avoid CSS specificity conflicts */

/* Focus states moved to individual selectors to avoid CSS specificity conflicts */

/* Loading states for dynamic content */
.loading {
    opacity: 0.7;
    pointer-events: none;
}

.loading::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 20px;
    height: 20px;
    margin: -10px 0 0 -10px;
    border: 2px solid #e9e9e7;
    border-top: 2px solid #0f70b8;
    border-radius: 50%;
    animation: spin 1s linear infinite;
}

/* Focus transitions consolidated above for CSS specificity ordering */

/* Enhanced shadows for depth */
.elevated {
    box-shadow: 0 2px 8px rgb(0 0 0 / 8%);
}

.elevated-hover:hover {
    box-shadow: 0 4px 16px rgb(0 0 0 / 12%);
}

/* Error Logs Section */
.error-logs-section {
    flex: 1;
    display: flex;
    flex-direction: column;
    background: #fff;
}

.error-controls {
    display: flex;
    gap: 8px;
}

.error-stats {
    display: flex;
    gap: 16px;
    padding: 16px 20px;
    background: #f7f6f3;
    border-bottom: 1px solid #e9e9e7;
}

.stat-item {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.stat-label {
    font-size: 0.75rem;
    font-weight: 500;
    color: #9b9a97;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.stat-value {
    font-size: 1.125rem;
    font-weight: 600;
    color: #37352f;
}

.error-filters {
    display: flex;
    padding: 12px 20px;
    background: #fff;
    border-bottom: 1px solid #e9e9e7;
    gap: 8px;
}

.filter-btn {
    padding: 6px 12px;
    border: 1px solid #e9e9e7;
    background: #fff;
    color: #9b9a97;
    font-size: 0.8125rem;
    font-weight: 500;
    border-radius: 4px;
    cursor: pointer;
    transition: all 0.15s ease;
}

.filter-btn:hover {
    background: #f7f6f3;
    border-color: #d3d1cb;
}

.filter-btn.active {
    background: #0f70b8;
    color: white;
    border-color: #0f70b8;
}

.error-log-container {
    flex: 1;
    overflow: hidden;
}

/* .error-log consolidated above for CSS specificity ordering */

.no-errors {
    text-align: center;
    padding: 40px 20px;
    color: #9b9a97;
}

.no-errors-icon {
    font-size: 2rem;
    display: block;
    margin-bottom: 12px;
}

.no-errors p {
    font-size: 1rem;
    font-weight: 500;
    margin-bottom: 8px;
    color: #37352f;
}

.no-errors small {
    font-size: 0.8125rem;
    color: #9b9a97;
}

.error-item {
    padding: 12px 16px;
    background: #fff;
    border: 1px solid #e9e9e7;
    border-left: 4px solid #ef4444;
    border-radius: 6px;
    margin-bottom: 10px;
    transition: all 0.15s ease;
}

.error-item:hover {
    background: #fafafa;
    box-shadow: 0 2px 8px rgb(0 0 0 / 6%);
}

.error-item.warning {
    border-left-color: #f59e0b;
}

.error-item.info {
    border-left-color: #06b6d4;
}

.error-header {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin-bottom: 8px;
}

.error-type {
    font-size: 0.75rem;
    font-weight: 600;
    padding: 2px 6px;
    border-radius: 3px;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.error-type.error {
    background: #fef2f2;
    color: #991b1b;
}

.error-type.warning {
    background: #fef3c7;
