        // Check if Chrome needs installation (only if not running)
        let needsInstallation = false;
        if (!chromeRunning) {
          needsInstallation = !(await this.chromeLauncher.isChromeInstalled());
        }

        return {
          chromeRunning,
          chromePid,
          debugPort,
          chromeResponsive,
          websocketRunning,
          slitherConnected,
          activeConnections,
          sessions,
          error: null,
          needsInstallation,
        };
