const { app, BrowserWindow, ipcMain, dialog } = require('electron');
const path = require('path');
const os = require('os');
const pty = require('node-pty');
const CDP = require('chrome-remote-interface');
const NativeChromeManager = require('./chrome-manager/native-chrome-launcher');
const ConnectionPool = require('./chrome-manager/connection-pool');

// Enforce single instance of the application
const gotTheLock = app.requestSingleInstanceLock();

if (!gotTheLock) {
  app.quit();
} else {
  app.on('second-instance', (_event, _commandLine, _workingDirectory) => {
    // Someone tried to run a second instance, we should focus our window.
    if (containerApp.mainWindow) {
      if (containerApp.mainWindow.isMinimized()) containerApp.mainWindow.restore();
      containerApp.mainWindow.focus();
    }
  });
}

// Global error handlers to prevent crashes from EPIPE and other stream errors
process.on('uncaughtException', (error) => {
  if (error.code === 'EPIPE' || error.errno === 'EPIPE') {
    // Ignore EPIPE errors (broken pipe to console/stdout)
    console.error('âš ï¸ EPIPE error ignored:', error.message);
    return;
  }
  // Log other uncaught exceptions but don't crash
  console.error('âŒ Uncaught Exception:', error);
});

process.on('unhandledRejection', (reason, promise) => {
  console.error('âŒ Unhandled Rejection at:', promise, 'reason:', reason);
});

// Safe logging utility to prevent EPIPE errors
function safeLog(...args) {
  try {
    console.log(...args);
  } catch {
    // Ignore EPIPE and other console write errors
  }
}

function _safeError(...args) {
  try {
    console.error(...args);
  } catch {
    // Ignore EPIPE and other console write errors
  }
}

class SlitherChromeContainer {
  constructor() {
    this.mainWindow = null;
    // Pass error callback to Chrome launcher for stderr forwarding
    this.chromeLauncher = new NativeChromeManager({
      errorCallback: (error) => this.forwardChromeError(error),
    });
    this.connectionPool = new ConnectionPool();
    this.chromeInstance = null;
    this.messageQueue = [];
    this.ptyProcess = null; // Real terminal process
    this.terminalProcesses = new Map(); // Multiple terminal processes
    this.metricsInterval = null; // Live metrics timer
    this.cdpClient = null; // CDP connection client
    this.terminalState = {
      cwd: process.cwd(),
      history: [],
      environment: {},
      pid: null,
      // CLAUDE CONTEXT DATA - All app state accessible in terminal
      claude: {
        appState: {
          chrome: {
            running: false,
            pid: null,
            port: null,
            instances: 0,
            responsive: false,
          },
          websocket: {
            running: false,
            port: 9223,
            connections: 0,
            sessions: [],
          },
          terminal: {
            active: false,
            cols: 80,
            rows: 30,
            shell: null,
          },
          ui: {
            activeTab: 'terminal',
            windowExists: false,
            devToolsOpen: false,
          },
          errors: {
            total: 0,
            chrome: 0,
            app: 0,
            recent: [],
          },
          performance: {
            cpu: 0,
            memory: 0,
            chromeTabs: 0,
            uptime: 0,
          },
        },
        commands: {
          // Claude-accessible commands
          'claude-status': 'Get full application status',
          'claude-chrome': 'Chrome management commands',
          'claude-websocket': 'WebSocket management commands', 
          'claude-errors': 'Error log management',
          'claude-export': 'Export application data',
          'claude-navigate': 'Navigate to canvas or URLs',
          'claude-screenshot': 'Take application screenshots',
          'claude-restart': 'Restart components safely',
        },
        files: {
          config: './package.json',
          state: './terminal-state.json',
          logs: './container-logs-*.json',
          main: './main.js',
          renderer: './renderer/app.js',
          chrome: './chrome-manager/',
        },
        lastUpdate: Date.now(),
      },
    }; // Terminal state for persistence with Claude context
    this.setupSignalHandlers(); // Setup PKILL and other signal handlers
  }

  // Setup signal handlers for graceful shutdown and state persistence
  setupSignalHandlers() {
    console.log('ğŸ›¡ï¸ Setting up signal handlers for terminal state persistence...');

    // Handle PKILL signals (SIGTERM, SIGINT, SIGQUIT)
    const signalsToHandle = ['SIGTERM', 'SIGINT', 'SIGQUIT', 'SIGKILL'];

    signalsToHandle.forEach(signal => {
      if (signal !== 'SIGKILL') { // Can't catch SIGKILL
        process.on(signal, async () => {
          console.log(`ğŸš¨ Received ${signal}, performing graceful shutdown with state preservation...`);
          await this.saveTerminalState();
          await this.cleanup();
          process.exit(0);
        });
      }
    });

    // Handle unexpected exit scenarios
    process.on('exit', async (code) => {
      console.log(`ğŸ”š Process exiting with code ${code}, saving terminal state...`);
      await this.saveTerminalState();
    });

    // Handle uncaught exceptions with state saving
    process.on('uncaughtException', async (error) => {
      if (error.code === 'EPIPE' || error.errno === 'EPIPE') {
        console.error('âš ï¸ EPIPE error ignored:', error.message);
        return;
      }
      console.error('âŒ Uncaught Exception, saving state before exit:', error);
      await this.saveTerminalState();
      process.exit(1);
    });

    console.log('âœ… Signal handlers setup complete');
  }

  // Save terminal state to persistent storage
  async saveTerminalState() {
    try {
      console.log('ğŸ’¾ Saving terminal state...');

      const fs = require('fs');
      const path = require('path');

      // Update current terminal state
      if (this.ptyProcess) {
        this.terminalState.pid = this.ptyProcess.pid;
        this.terminalState.cwd = this.ptyProcess.process?.cwd || process.cwd();
      }

      // Add timestamp
      this.terminalState.savedAt = new Date().toISOString();
      this.terminalState.appVersion = require('./package.json').version;

      const stateFile = path.join(__dirname, 'terminal-state.json');

      await fs.promises.writeFile(
        stateFile, 
        JSON.stringify(this.terminalState, null, 2),
        'utf8',
      );

      console.log(`âœ… Terminal state saved to ${stateFile}`);
      return true;
    } catch (_error) {
      console.error('âŒ Failed to save terminal state:', _error);
      return false;
    }
  }

  // Restore terminal state from persistent storage
  async restoreTerminalState() {
    try {
      console.log('ğŸ“‚ Attempting to restore terminal state...');

      const fs = require('fs');
      const path = require('path');
      const stateFile = path.join(__dirname, 'terminal-state.json');

      if (!fs.existsSync(stateFile)) {
        console.log('â„¹ï¸ No previous terminal state found, starting fresh');
        return false;
      }

      const stateData = await fs.promises.readFile(stateFile, 'utf8');
      const savedState = JSON.parse(stateData);

      // Validate state data
      if (!savedState || typeof savedState !== 'object') {
        console.warn('âš ï¸ Invalid terminal state data, starting fresh');
        return false;
      }

      // Check if state is not too old (more than 24 hours)
      const stateAge = Date.now() - new Date(savedState.savedAt).getTime();
      if (stateAge > 24 * 60 * 60 * 1000) {
        console.log('â° Terminal state is too old (>24h), starting fresh');
        // Clean up old state file
        fs.unlinkSync(stateFile);
        return false;
      }

      // Restore state
      this.terminalState = {
        ...this.terminalState,
        ...savedState,
        pid: null, // Don't restore PID as process is dead
      };

      console.log(`âœ… Terminal state restored from ${savedState.savedAt}`);
      console.log(`ğŸ“ Restored CWD: ${this.terminalState.cwd}`);

      return true;
    } catch (_error) {
      console.error('âŒ Failed to restore terminal state:', _error);
      return false;
    }
  }

  // Update Claude context with current application state
  updateClaudeContext() {
    try {
      // Update Chrome state
      this.terminalState.claude.appState.chrome = {
        running: this.chromeInstances && this.chromeInstances.length > 0,
        pid: this.chromeInstance?.pid || null,
        port: this.chromeInstance?.port || null,
        instances: this.chromeInstances?.length || 0,
        responsive: this.cdpClient !== null,
      };

      // Update WebSocket state
      this.terminalState.claude.appState.websocket = {
        running: this.connectionPool?.isServerRunning() || false,
        port: 9223,
        connections: this.connectionPool?.getActiveConnectionCount() || 0,
        sessions: this.connectionPool?.getActiveSessions() || [],
      };

      // Update terminal state
      this.terminalState.claude.appState.terminal = {
        active: this.ptyProcess !== null,
        cols: 80,
        rows: 30,
        shell: this.ptyProcess ? (os.platform() === 'win32' ? 'powershell' : 'bash') : null,
        pid: this.ptyProcess?.pid || null,
      };

      // Update UI state
      this.terminalState.claude.appState.ui = {
        activeTab: 'terminal', // Default, can be updated from renderer
        windowExists: this.mainWindow && !this.mainWindow.isDestroyed(),
        devToolsOpen: this.mainWindow?.webContents.isDevToolsOpened() || false,
      };

      // Update performance metrics
      const memoryUsage = process.memoryUsage();
      this.terminalState.claude.appState.performance = {
        cpu: 0, // Will be updated by gatherMetrics
        memory: Math.round(memoryUsage.heapUsed / 1024 / 1024),
        chromeTabs: 0, // Will be updated by gatherMetrics
        uptime: Math.round(process.uptime()),
      };

      // Update timestamp
      this.terminalState.claude.lastUpdate = Date.now();

      // Export to environment variables for Claude's easy access
      this.updateClaudeEnvironment();

      return true;
    } catch (_error) {
      console.warn('âš ï¸ Failed to update Claude context:', _error.message);
      return false;
    }
  }

  // Export Claude context to environment variables
  updateClaudeEnvironment() {
    try {
      const claude = this.terminalState.claude;

      // Core app state as environment variables
      this.terminalState.environment = {
        ...this.terminalState.environment,

        // Chrome state
        CLAUDE_CHROME_RUNNING: claude.appState.chrome.running.toString(),
        CLAUDE_CHROME_PID: claude.appState.chrome.pid?.toString() || '',
        CLAUDE_CHROME_PORT: claude.appState.chrome.port?.toString() || '',
        CLAUDE_CHROME_INSTANCES: claude.appState.chrome.instances.toString(),

        // WebSocket state
        CLAUDE_WS_RUNNING: claude.appState.websocket.running.toString(),
        CLAUDE_WS_PORT: claude.appState.websocket.port.toString(),
        CLAUDE_WS_CONNECTIONS: claude.appState.websocket.connections.toString(),

        // Terminal state
        CLAUDE_TERMINAL_ACTIVE: claude.appState.terminal.active.toString(),
        CLAUDE_TERMINAL_SHELL: claude.appState.terminal.shell || '',
        CLAUDE_TERMINAL_PID: claude.appState.terminal.pid?.toString() || '',

        // UI state
        CLAUDE_UI_WINDOW_EXISTS: claude.appState.ui.windowExists.toString(),
        CLAUDE_UI_DEVTOOLS_OPEN: claude.appState.ui.devToolsOpen.toString(),

        // Performance
        CLAUDE_MEMORY_MB: claude.appState.performance.memory.toString(),
        CLAUDE_UPTIME_SECONDS: claude.appState.performance.uptime.toString(),

        // File paths
        CLAUDE_APP_ROOT: process.cwd(),
        CLAUDE_STATE_FILE: path.join(process.cwd(), 'terminal-state.json'),
        CLAUDE_CONFIG_FILE: path.join(process.cwd(), 'package.json'),

        // Timestamp
        CLAUDE_LAST_UPDATE: claude.lastUpdate.toString(),

        // JSON data for complex queries
        CLAUDE_APP_STATE_JSON: JSON.stringify(claude.appState),
        CLAUDE_COMMANDS_JSON: JSON.stringify(claude.commands),
        CLAUDE_FILES_JSON: JSON.stringify(claude.files),
      };

      return true;
    } catch (_error) {
      console.warn('âš ï¸ Failed to update Claude environment:', _error.message);
      return false;
    }
  }

  async createMainWindow() {
    this.mainWindow = new BrowserWindow({
      width: 1400,
      height: 900,
      webPreferences: {
        preload: path.join(__dirname, 'preload.js'),
        contextIsolation: true,  // Secure by default
        nodeIntegration: false,  // Secure by default
        webviewTag: true,         // Enable <webview> for Chrome DevTools
      },
      icon: path.join(__dirname, 'assets', 'icon.png'),
      title: 'Slither Chrome Container v1.0',
    });

    await this.mainWindow.loadFile('renderer/index.html');

    // Clear cache for development but avoid aggressive reload that causes webview race conditions
    this.mainWindow.webContents.session.clearCache();
    // REMOVED: this.mainWindow.webContents.reloadIgnoringCache(); // This causes ERR_ABORTED webview errors

    // Enable dev tools to debug preload issues
    this.mainWindow.webContents.openDevTools();

    // Handle window closed - but don't cleanup, container should stay running
    this.mainWindow.on('closed', () => {
      console.log('ğŸ”„ Main window closed, but container continues running...');
      this.mainWindow = null;
    });
  }

  // Wait for Chrome to be ready before accepting connections
  async waitForChromeReady(port, timeout = 10000) {
    const startTime = Date.now();
    while (Date.now() - startTime < timeout) {
      try {
        const response = await fetch(`http://localhost:${port}/json/version`);
        if (response.ok) {
          console.log(`âœ… Chrome on port ${port} is ready.`);
          return true;
        }
      } catch {
        // Ignore connection errors and retry
      }
      // Wait 200ms before trying again
      await new Promise(resolve => setTimeout(resolve, 200));
    }
    throw new Error(`Chrome on port ${port} did not become ready in time.`);
  }

  async connectToChrome(port) {
    if (this.cdpClient) {
      try {
        await this.cdpClient.close();
      } catch {
        // Ignore errors closing old client
      }
      this.cdpClient = null;
    }

    try {
      console.log(`ğŸ”— Connecting to Chrome on port ${port} using CDP...`);

      // Get available targets first
      const targets = await CDP.List({ port });
      console.log(`ğŸ“‹ Found ${targets.length} Chrome targets`);

      // Find the first page target (not extension, service worker, etc.)
      const pageTarget = targets.find(target => target.type === 'page') || targets[0];

      if (!pageTarget) {
        throw new Error('No suitable Chrome target found');
      }

      console.log(`ğŸ¯ Connecting to target: ${pageTarget.title || pageTarget.url}`);

      // Connect to the specific target
      this.cdpClient = await CDP({ port, target: pageTarget });

      // Enable all necessary domains for DevTools compatibility
      console.log('ğŸ”§ Enabling Chrome DevTools Protocol domains...');
      await Promise.all([
        this.cdpClient.Page.enable(),
        this.cdpClient.Runtime.enable(),
        this.cdpClient.DOM.enable(),
        this.cdpClient.CSS.enable(),
        this.cdpClient.Network.enable(),
        this.cdpClient.Debugger.enable(),
        this.cdpClient.Target.setDiscoverTargets({ discover: true }),
      ]).catch(error => {
        console.warn('âš ï¸ Some CDP domains failed to enable:', error.message);
        // Continue anyway - some domains might not be available
      });

      // Listen for disconnection events
      this.cdpClient.on('disconnect', () => {
        console.log('ğŸ”´ CDP client disconnected.');
        this.cdpClient = null;
        this.updateRendererStatus();
      });

      // **ADD THIS BLOCK TO LISTEN FOR BACKEND ERRORS**
      this.cdpClient.on('error', (err) => {
        console.error('ğŸ”´ CDP Client Error:', err);
        const errorObject = {
          source: 'CDP Backend',
          message: err.message,
          timestamp: new Date().toISOString(),
          type: 'error',
        };
        if (this.mainWindow && !this.mainWindow.isDestroyed()) {
          this.mainWindow.webContents.send('new-error-log', errorObject);
        }
      });

      console.log('âœ… CDP connection successful.');
      return true;
    } catch (_error) {
      console.error('âŒ CDP connection failed:', _error);
      this.cdpClient = null;
      return false;
    }
  }

  async initializeChrome() {
    // **ANTI-GHOSTING FIX**: Always kill any lingering instances before starting.
    console.log('ğŸ§¹ Checking for and killing any lingering Chrome instances...');
    if (this.chromeInstances && this.chromeInstances.length > 0) {
      const instancesToKill = [...this.chromeInstances];
      for (const instance of instancesToKill) {
        await this.chromeLauncher.killChromeInstance(instance);
      }
      await new Promise(resolve => setTimeout(resolve, 500)); // Give processes time to die
      this.chromeInstances = [];
      this.chromeInstance = null;
      console.log('âœ… Lingering instances cleaned up.');
    }

    try {
      console.log('ğŸš€ Initializing a new Chrome instance...');
      if (!this.chromeInstances) this.chromeInstances = [];

      const chromeResult = await this.chromeLauncher.launchChrome();
      if (!chromeResult.success) throw new Error(chromeResult.error || 'Failed to launch Chrome');

      const newInstance = chromeResult.chromeInstance;
      await this.waitForChromeReady(newInstance.port);

      this.chromeInstances.push(newInstance);
      if (!this.chromeInstance) this.chromeInstance = newInstance;
      if (!this.connectionPool.isInitialized()) await this.connectionPool.initialize(this.chromeInstance.port);

      // Establish CDP connection for stable WebSocket communication
      await this.connectToChrome(newInstance.port);

      console.log(`âœ… Chrome instance #${this.chromeInstances.length} launched on port: ${newInstance.port} (PID: ${newInstance.pid})`);

      newInstance.process.on('exit', (code) => {
        console.log(`ğŸ”´ Chrome instance with PID ${newInstance.pid} exited with code: ${code}.`);
        this.chromeInstances = this.chromeInstances.filter(inst => inst.pid !== newInstance.pid);
        if (this.chromeInstance && this.chromeInstance.pid === newInstance.pid) {
          this.chromeInstance = this.chromeInstances[0] || null;
        }
        this.updateRendererStatus();
      });

      this.updateRendererStatus();
      return { success: true };
    } catch (_error) {
      console.error('âŒ Failed to initialize Chrome:', _error);
      this.updateRendererStatus({ error: _error.message });
      return { success: false, error: _error.message };
    }
  }

  setupIPC() {
    console.log('ğŸ”Œ Setting up IPC handlers...');

    // Handle get chrome status request (register this first)
    console.log('ğŸ“ Registering handler: get-chrome-status');
    ipcMain.handle('get-chrome-status', async (_event) => {
      try {
        const isRunning = this.chromeInstances && this.chromeInstances.length > 0;
        const status = {
          running: isRunning,
          pid: this.chromeInstance?.pid || null,
          port: this.chromeInstance?.port || null,
          instanceCount: this.chromeInstances?.length || 0,
          memory: Math.round(process.memoryUsage().heapUsed / 1024 / 1024),
        };

        safeLog('ğŸ“¡ Sending chrome status:', status);
        return status;
      } catch (_error) {
        console.error('âŒ Error getting chrome status:', _error);
        return {
          running: false,
          error: _error.message,
          instanceCount: 0,
        };
      }
    });

    // Handle DevTools reconnection (full implementation)
    console.log('ğŸ“ Registering handler: reconnect-devtools');
    ipcMain.handle('reconnect-devtools', async (_event) => {
      try {
        console.log('ğŸ”— Reconnecting to Chrome DevTools...');

        if (!this.chromeInstance || !this.chromeInstance.port) {
          throw new Error('No Chrome instance available to reconnect to');
        }

        // Close existing CDP connection if it exists
        if (this.cdpClient) {
          try {
            await this.cdpClient.close();
            console.log('ğŸ”´ Closed existing CDP connection');
          } catch (_error) {
            console.log('âš ï¸ Error closing old CDP connection (ignored):', _error.message);
          }
          this.cdpClient = null;
